INSERT INTO `foods` VALUES (179, '香脆炸虾', '/api/img/2021100116330536423931958079.jpg', 43.00, '热菜', '原料：草虾、鸡蛋、面包糠、玉米淀粉、生姜、面粉、盐、料酒、胡椒粉。', '“老是做白灼虾，吃腻了，今天换点香脆的。颜值很高，宴客菜品。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (180, '盐焗鸡翅', '/api/img/2021100116330535073961958079.jpg', 14.00, '热菜', '原料：鸡翅、盐焗粉、黄栀子、盐、生姜、料酒、花椒。', '“特别喜欢吃盐焗味的东西。年年都在种植箱中种上一点沙姜，爱极了它的味，闻着就很治愈，吃多也不腻。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (181, '桂花红烧肉', '/api/img/2021093016329906737081958079.jpg', 26.00, '热菜', '原料：五花肉、大葱、大蒜、植物油、热水、桂花红烧酱调味料包、干桂花。', '“天一凉，人就馋，这些日子我连着做了好几顿红烧肉，有自己调汁配料的，有用现成的酱料包的，各有千秋，都好吃。今儿这锅红烧肉用的桂花红烧酱料，色泽红润诱人，口感咸中带甜，撒上干桂花不但看着赏心悦目，吃着还有桂花香，与甜味相呼应，真让人胃口大开。” 					', NULL, NULL, 1);
