INSERT INTO `foods` VALUES (230, '凉拌莲藕丝', '/api/img/2021100516334449821021958079.jpg', 60.00, '凉菜', '原料：莲藕、青椒、洋葱、白醋、白糖、红椒、葱花、蒜蓉、盐鸡精、生抽、香醋、辣椒油、香油、香菜段。', '““莲藕”莲藕别再炒菜吃了。教你一个清脆、爽口的莲藕切细丝凉拌的吃法。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (231, '糖醋樱桃萝卜', '/api/img/2021100516334346124131958079.jpg', 26.00, '凉菜', '原料：樱桃水萝卜、小米辣、盐、绵白糖、白米醋。', '“今儿分享的是萝卜头的吃法，做法在下面都有详细说明。多说两句那绿樱子哈，那绿樱子有一股辣气，洗净蘸酱吃或者切碎加点炸酱，再或者加点生抽、盐、醋同拌，开胃、通气、补充维生素C、E和丰富的膳食纤维。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (232, '自制午餐肉', '/api/img/2021100516334341981231958079.jpg', 18.00, '凉菜', '原料：猪精肉、葱姜水、淀粉、蛋清、食盐、黑胡椒粉、鸡精、生抽。', '“我喜欢有颗粒感的，所以肉馅打的不够细腻，肉馅不细腻，放的淀粉又少，做出来的午餐肉有蜂窝，看起来不美，味道真心不差的。” 					', NULL, NULL, 1);
