INSERT INTO `foods` VALUES (287, '炝拌旱黄瓜', '/api/img/2021090916311511686618197577.jpg', 57.00, '凉菜', '原料：旱黄瓜、大蒜、盐、白糖、鸡精、生抽、陈醋、花椒油、小米辣。', '“旱黄瓜较短，同乳瓜相比，长度相差不大，长你比较粗，质地更加脆嫩微甜，生吃口感更好。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (288, '油炸花生拌黄瓜', '/api/img/2021090816311066616468197577.jpg', 18.00, '凉菜', '原料：花生米、黄瓜、大蒜、辣椒粉、芝麻、油、盐、醋、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (289, '凉拌蒜泥茄子，不过油、不油炸的茄子还能这么好吃！做法简单10分钟就搞定，拿肉也不换', '/api/img/2021090716310035022261958079.jpg', 40.00, '凉菜', '原料：茄子、小米辣、香菜、蒜、芝麻、酱油、醋、香油、糖。', '“凉拌蒜泥茄子，吃起来酸辣咸香回口带有微微甜，其中蒜末的加入简直是灵魂，它让原本味道稍显寡淡的茄子出彩，变得超级诱人，而且开胃下饭，还解腻。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (290, '拌海带丝', '/api/img/2021090716309977041421958079.jpg', 52.00, '凉菜', '原料：干海带、胡萝卜、洋葱、鲜酱油、蚝油、白糖、蒜末、小米辣、葱花。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (291, '凉拌盐焗鸡胗', '/api/img/2021090616309219011561958079.jpg', 41.00, '凉菜', '原料：鸡胗、青瓜、盐焗鸡粉、芝麻、蒸鱼豉油、麻油。', '“凉拌小菜一碟。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (292, '凉拌猪耳朵娃娃菜', '/api/img/2021090616309187209871958079.jpg', 40.00, '凉菜', '原料：娃娃菜、熟猪耳朵、盐、生抽、芝麻油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (293, '酸辣开胃的凉拌菠菜', '/api/img/2021090616309170627461958079.jpg', 43.00, '凉菜', '原料：菠菜、胡萝卜、大蒜、生抽、醋、辣椒油。', '“立秋之后，虽然天气早晚凉爽了不少，但是 “秋老虎”的威力还未消，在饮食上还要注意荤素搭配，适当吃些富含植物纤维的小凉菜，不但开胃，还能清理肠胃、顺气，不上火。这道凉拌菠菜就很适合现在食用，与胡萝卜丝同拌颜色漂亮，营养丰富，口感清脆，适合大多数人群。脾胃虚弱的人可将胡萝卜丝焯烫后再拌。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (294, '黄瓜拌皮蛋', '/api/img/2021090516308260083961958079.jpg', 47.00, '凉菜', '原料：黄瓜、皮蛋、洋葱、红油。', '“没有凉菜的，夏天是不完美的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (295, '超级下饭的快手凉拌豆干', '/api/img/2021090316306776213458197577.jpg', 15.00, '凉菜', '原料：豆腐干、葱、姜、蒜、花椒面、辣椒油、熟芝麻、生抽、糖、味精、盐、热油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (296, '茼蒿拌虾仁', '/api/img/2021090316306610367638197577.jpg', 45.00, '凉菜', '原料：鲜虾、茼蒿、胡萝卜、葱、蒜、红辣椒、油、盐、油泼辣子、酱油、芝麻。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (297, '凉拌蒸茄子', '/api/img/2021090316306556491631958079.jpg', 29.00, '凉菜', '原料：茄子、食用油、蒜末、葱、酱油、醋、白芝麻、蚝油、盐、白醋、辣椒面。', '“夏天必备的一道菜，不想吃太油腻的，可以试试！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (298, '泰汁拌鸡爪', '/api/img/2021090316306522076521958079.jpg', 46.00, '凉菜', '原料：鸡爪、柠檬、大蒜、洋葱、泰汁料包。', '“外面卖的泰汁鸡爪真的是太好吃了，经过几次的做法都失败了，今天终于把它做成功。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (299, '茄汁腐竹', '/api/img/2021090316306412068298197577.jpg', 14.00, '凉菜', '原料：腐竹、鸡蛋、香菇、葱、姜、蒜、香菜、干辣椒、盐、味精、鸡精、白糖、十三香、酱油。', '“为自己做饭是一种美德，为家人做饭是一种修行，从一个人到一个家，不仅要做自己喜欢的菜，更要做家人喜欢的菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (300, '蒜粒拌绿豆角', '/api/img/2021090316306374875358197577.jpg', 18.00, '凉菜', '原料：绿豆角、蒜粒、辣椒粉、辣椒油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (301, '酸辣鸡爪', '/api/img/2021090316306330052668197577.jpg', 52.00, '凉菜', '原料：鸡爪、香葱、大蒜、桂皮、八角、姜、白芝麻、香醋、芝麻油、盐、生抽、老抽、自制油辣椒。', '“粗茶淡饭最养人，平平淡淡才是真。记录生活中的点点滴滴！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (302, '凉拌豆角丝', '/api/img/2021090216305844207298197577.jpg', 25.00, '凉菜', '原料：江豆、食用油、干红辣椒、蒜瓣、盐、生抽、耗油、香醋、麻油、白糖、鸡精。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (303, '孩子特别喜欢的葫芦娃～秋葵土豆泥', '/api/img/2021090216305660209301958079.jpg', 60.00, '凉菜', '原料：土豆、秋葵、胡萝卜、味达美醇香米醋、白糖、淀粉、纯净水、盐。', '“5元钱的秋葵和土豆，做成你吃不起的样子；酸甜开胃，上桌一抢而光。孩子特别喜欢，制作非常简单。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (304, '熟肉拌苦瓜', '/api/img/2021090216305607392051958079.jpg', 54.00, '凉菜', '原料：熟肉、苦瓜、辣椒、食盐、鸡精。', '“煮的熟肉备着，用来拌菜既清爽美味又营养健康。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (305, '无油开胃菜，百香果柠檬鸡胸肉丝', '/api/img/2021090216305466234438197577.jpg', 44.00, '凉菜', '原料：鸡胸肉、百香果复合水果饮料浓浆、玉米粒、柠檬、干辣椒、葱、盐、料酒、生抽、迷迭香。', '“白露将至，夏日已尾声，还是很热。没什么胃口，一道百香果柠檬鸡丝，开开胃，解解暑气。酸甜口，不刺激，这个时节吃正合适。意外的合胃口，和小朋友一起吃了好多，酸甜清爽，关键是无油！放心吃！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (306, '鸽胸肉拌马齿苋', '/api/img/2021090116304995924561958079.jpg', 56.00, '凉菜', '原料：马齿苋、鸽胸肉、食盐、鸡精、大蒜、生抽、陈醋。', '“院子里的马齿苋让老公当野草给我拔掉扔了，这不在市场遇到了，赶紧买了5毛钱的，加上鸽胸肉，足够我一顿吃的了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (307, '凉拌灰圪针', '/api/img/2021090116304896005648197577.jpg', 37.00, '凉菜', '原料：灰圪针、小米椒、香油、盐、醋。', '“要想身体好，就得吃点草。灰圪针又名鬼圪针降血脂，降血压，最主要它是美味山野菜。健康营养快手菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (308, '卤牛肉', '/api/img/2021090116304744333088197577.jpg', 59.00, '凉菜', '原料：牛腱子、豆瓣酱、大葱、姜片、料酒、生抽、老抽、冰糖、白芷、草果、桂皮、香叶、丁香、花椒、大料、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (309, '松花蛋拌秋葵', '/api/img/2021090116304660607578197577.jpg', 33.00, '凉菜', '原料：秋葵、松花蛋、剁椒、蒜末、盐、白糖、蒸鱼豉油、蚝油、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (310, '皮蛋拌豆腐', '/api/img/2021083016302868918708197577.jpg', 23.00, '凉菜', '原料：内酯豆腐、松花皮蛋、乌江榨菜、葱、蒜头、花生油、辣椒面、生抽、香醋、白糖。', '“之前买的一盒皮蛋，本来是想煮皮蛋瘦肉粥，结果一次都没煮，搭配绿豆粥快吃完了，剩下最后2个，今天拿来做皮蛋拌豆腐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (311, '鸡蛋拌黄瓜', '/api/img/2021082916302384434648197577.jpg', 17.00, '凉菜', '原料：黄瓜、鸡蛋、油醋汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (312, '香嫩开胃的凉拌牛肉', '/api/img/2021082916302262447968197577.jpg', 22.00, '凉菜', '原料：瘦牛肉、胡萝卜、香菜、大蒜、小葱、玉米淀粉、醋、生抽、白糖、香油或辣椒油。', '“牛肉好吃，但传统的炖牛肉没有几个小时甭打算鼓捣熟，嘴馋心急可咋办？最近很火的凉拌牛肉真不赖，我也做了一回，虽然没有炖牛肉那般醇厚，但20分钟立马儿吃上，解馋又省事。这个拌牛肉的原理很像是涮羊肉，通过焯烫使肉片在短时间内成熟，还保持了牛肉的口感和营养。虽然今儿做的酸辣口儿的，但刚说了跟涮羊肉原理很像，所以也可以尝试做成麻酱口儿或者其它口味的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (313, '潮汕特色美食生腌血蛤', '/api/img/2021082816301364349671958079.jpg', 23.00, '凉菜', '原料：血蛤、蒜蓉、小米辣、姜、香菜、白砂糖、醋、盐、酱油。', '“最近有很多朋友私信我写我们潮汕生腌海鲜的做法，生腌是我们潮汕美食特色之一，海鲜生腌也深受我们潮汕人喜爱的一道美食，生虾、螃蟹、皮皮虾等海鲜搭配调好的调汁腌制，味道特别鲜美让人回味无穷，今天浪险妹就来分享潮汕生腌血蛤的做法。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (314, '酸辣绿甘蓝', '/api/img/2021082716300284636318197577.jpg', 54.00, '凉菜', '原料：绿甘蓝、小米辣、泡野山椒、盐、白糖、味精、白醋、葱油、蒜片。', '“10分钟即可完成的懒人菜谱，经济实惠，酸辣脆爽。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (315, '炝拌扁豆芽', '/api/img/2021082716300283966078197577.jpg', 11.00, '凉菜', '原料：扁豆芽、青菜、红椒、盐、蒜蓉、白糖、味精、生抽、陈醋、现炸花椒油。', '“一款超简单的凉拌菜，只需开水焯上一分钟，投凉沥干水分，简单加工即可。厨房小白亦可操作。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (316, '蘸汁黑豆腐', '/api/img/2021082616299858396291958079.jpg', 60.00, '凉菜', '原料：黑豆腐、香菜、蒜、葱、油、盐、醋、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (317, '凉拌面筋', '/api/img/2021082616299640625028197577.jpg', 43.00, '凉菜', '原料：面筋、洋葱、葱、蒜、香菜、红辣椒、盐、油、醋、酱油、芝麻。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (318, '凉拌鸡翅根', '/api/img/2021082616299558998198197577.jpg', 42.00, '凉菜', '原料：鸡翅根、香菜、白芝麻、葱花、蒜末、盐、鸡精、糖、生抽、老抽、芝麻油、香油、蚝油。', '“快手菜，简简单单，适合新手哦，喜欢就点个关注吧！感谢支持！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (319, '凉拌萝卜丝', '/api/img/2021082616299491937911958079.jpg', 31.00, '凉菜', '原料：红皮萝卜、葱、香菜、蒜、酱油、鸡精、蚝油、白糖、醋、烧烤辣子。', '“下饭菜，简单，小白也会做。米饭克星哦！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (320, '凉拌鸡丝', '/api/img/2021082616299418831881958079.jpg', 25.00, '凉菜', '原料：鸡腿、胡萝卜、葱、蒜头、白芝麻、八角、香菜、花生油、辣椒面、生抽、香醋、料酒、蚝油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (321, '蒜蓉金针菇', '/api/img/2021082616299417519778197577.jpg', 42.00, '凉菜', '原料：金针菇、蒜蓉、青椒、红椒、盐、蚝油、生抽、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (322, '凉拌金针菇', '/api/img/2021082616299415975118197577.jpg', 56.00, '凉菜', '原料：金针菇、枸杞、味达美柔香米醋、味达美尚品金标生抽。', '“秋天里需要多食一些酸性食物，中医有这样的说法，酸入肝，这是中医的一种理论，主要是来源于黄帝内经，也就是所谓的酸入肝，苦入心，甘入脾，心入肺，咸入肾，多进食酸性食物可以达到滋肝阴，养肝血的功效，也就是常说的以酸养肝。通过饮食的调整，可以防止肝血不足，平息肝火，促进胆汁生成，有利于疏泄肝胆湿热，从而使肝脏能够更好的发挥各项生理功能。今天这道凉拌金针菇，秋天需要多吃白色食物，而金针菇富含多糖和膳食纤维，可以清脂，也可以保护我们的肝脏，加入了醋进行凉拌，简单快手又健康。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (323, '自制豌豆凉粉 ', '/api/img/2021082216296396738971958079.jpg', 30.00, '凉菜', '原料：豌豆淀粉、水、杭椒、朝天椒、蒜、盐、生抽、香醋、藤椒油、花椒油、熟芝麻、糖、辣椒油。', '“每到夏季，是不是特别喜欢吃口凉拌菜，清新、凉爽，不像吃热菜那样大汗淋漓。凉粉有多种多样，不管什么成份的凉粉，炎热的夏季，大家都不会拒绝吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (324, '彩色大拉皮', '/api/img/2021082216296379272881958079.jpg', 58.00, '凉菜', '原料：东北拉皮、黄瓜、胡萝卜、鸡蛋、蒜末、芝麻酱、米醋、盐、尖辣椒、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (325, '“下饭菜”精肉木耳拌黄瓜', '/api/img/2021082216296374844871958079.jpg', 16.00, '凉菜', '原料：木耳、黄瓜、熟肉、油豆皮、大蒜、食盐、鸡精、白醋、生抽、香油。', '“最爽的下饭菜就是各种美味凉拌菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (326, '凉拌海带丝', '/api/img/2021082216296326476481958079.jpg', 23.00, '凉菜', '原料：海带、大蒜、生抽、香醋、麻油、红油、荆芥、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (327, '酱油鸽子', '/api/img/2021082216296164088211958079.jpg', 31.00, '凉菜', '原料：鸽子、蒜头、姜片、油、金兰酱油、糖。', '“色泽光亮，咸香味美，招呼客人也很体面的菜式。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (328, '自制宁波蟹糊', '/api/img/2021082216296154162161958079.jpg', 52.00, '凉菜', '原料：螃蟹、姜片、白酒、盐、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (329, '芹菜拌鸡胸肉', '/api/img/2021082216296148334071958079.jpg', 59.00, '凉菜', '原料：鸡胸肉、芹菜、姜片、盐、生抽、芝麻油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (330, '#下饭菜 # 麻酱拌粉皮', '/api/img/2021082216296147201651958079.jpg', 38.00, '凉菜', '原料：粉皮、麻酱、黄瓜、胡萝卜、蒜、鸡粉、白糖、海鲜酱油、芝麻油、米醋、辣椒油、蚝油、黑芝麻。', '“喜欢吃麻酱的，不怕胖的，夏季可以用麻酱拌各种菜，营养美味又易做。懒人必备下饭菜。这种买来的粉皮，之前多用来拌凉菜。突然想起用麻酱拌，一搜菜谱，还真不少，结合自家的口味及材料、调料简单拌了一下，味道还不错。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (331, '双丁拌茶蛋', '/api/img/2021082116295276091248197577.jpg', 17.00, '凉菜', '原料：茶蛋、黄瓜、胡萝卜、蒜、精盐、鸡粉、白糖、黑芝麻、芝麻油、白醋、海鲜酱油、辣椒油。', '“这应该算自创菜了，过程是这样婶滴：1、早餐经常吃煮鸡蛋或茶叶蛋，茶叶蛋有时直接吃掉；煮蛋有时得抹点酱或蘸点儿酱油。吃蛋时有时还得在盘边配点黄瓜片，既装饰又补充蔬菜。2、之前看过拌鸡蛋的吃法，木有动心，还觉得多此一举。3、一日突发奇想：茶蛋拌黄瓜、胡萝卜丁应该能不错，颜值肯定好，味道也差不了，还集合了蛋和蔬菜两类营养。搭配其他早餐时，可以当一道菜，吃起来也会很方便。4、试了一下，自感不错，以后可以成为早餐常菜。” 					', NULL, NULL, 1);
