INSERT INTO `foods` VALUES (335, '凉拌土豆芽菜', '/api/img/2021082016294611299961958079.jpg', 39.00, '凉菜', '原料：土豆、豆芽菜、香菜、尖辣椒丝、大蒜、芝麻、盐、蒜泥、辣椒粉、米醋、白糖、生抽、鸡精。', '“土豆作为万能美食，凉拌菜自然少不了它的亮相，今天它将携手纤细脆嫩的芽菜，让我们体会到爽口CP的魅力。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (336, '四合院家香丨松花皮冻', '/api/img/2021082016294487602198197577.jpg', 43.00, '凉菜', '原料：猪皮、糊辣椒面、油辣椒、葱段、黄豆。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (337, '开胃爽口的凉拌酸辣海带丝', '/api/img/2021082016294475463358197577.jpg', 51.00, '凉菜', '原料：鲜海带丝、胡萝卜、小葱、大蒜、小米辣、醋、生抽、香油。', '“今儿这道凉拌海带丝是我特别喜欢吃的，搭配的蔬菜除了胡萝卜，还可以用黄瓜、西葫芦、芹菜、青红辣椒，除了有增加色彩促进食欲外，还能补充多种营养素，尤其是丰富的植物纤维，那可真是太充足了。天凉快胃口就好，爱吃肉且进食量变大，难免会有点儿积食或者胃肠蠕动变慢，这道凉拌海带丝就是很好的胃肠“清道夫”。吃不了辣的就不放，大蒜小葱香油，调调味也不赖。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (338, '老干妈拌豆干', '/api/img/2021082016294389745301958079.jpg', 56.00, '凉菜', '原料：豆干、蒜末、香菜、香油、老干妈、糖、香醋、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (339, '花生猪皮冻', '/api/img/2021082016294232642191958079.jpg', 48.00, '凉菜', '原料：猪皮、花生、姜、干辣椒、八角、桂皮、香叶、草果、花椒、冰糖、盐、料酒、生抽。', '“花生加猪皮熬成皮冻，冰冰凉凉的，鲜香筋道，夏日的一道美味小菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (340, '凉拌菜花', '/api/img/2021082016294231223168197577.jpg', 50.00, '凉菜', '原料：菜花、胡萝卜、蒜、精盐、味精、白糖、海鲜酱油、芝麻油、米醋、辣椒油。', '“凉拌有别于炝拌，家里没有好多菜谱里的花椒粒，也就没浇热油。好在辣椒油里也有熟油，这样拌好后清清淡淡很适合夏季餐桌。” 					', NULL, NULL, 1);
