INSERT INTO `foods` VALUES (355, '凉拌手撕鸡', '/api/img/2021081816292525257681958079.jpg', 53.00, '凉菜', '原料：鸡腿、胡萝卜、洋葱、黄瓜、蒜末、香油适量、六月鲜10克轻盐原汁酱油、糖、醋、香油。', '“酸爽开胃凉拌手撕鸡，照顾到老人孩子的需求，没有放辣椒，全家都可以享用。” 					', NULL, NULL, 1);
