INSERT INTO `foods` VALUES (365, '凉拌苦瓜', '/api/img/2021081316288353926838197577.jpg', 40.00, '凉菜', '原料：苦瓜、橄榄油、盐、味精。', '“苦瓜有清热、去心火、解毒明目、补气益精、止渴消暑的作用。夏季天气炎热，多食苦瓜好处多。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (366, '凉拌黑木耳', '/api/img/2021081316288352334698197577.jpg', 16.00, '凉菜', '原料：黑木耳、大蒜、老干妈辣椒酱、生抽、盐、醋、白糖、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (367, '鸡胸肉这样做分分钟光盘！香辣过瘾超下饭', '/api/img/2021081316288342988878197577.jpg', 35.00, '凉菜', '原料：鸡胸肉、小米辣、香菜、料酒、生抽、蚝油。', '“又解锁鸡胸肉的灵魂吃法~凉拌鸡胸肉!你们一定要去试试~香辣过瘾，比牛肉还好吃，真的太解馋了!关键还好吃不胖!赶紧安排吧” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (368, '凉拌苦瓜', '/api/img/2021081216287736187911958079.jpg', 24.00, '凉菜', '原料：苦瓜、食用油、蒜头、食盐、白糖、自制花生酱、青柠酸辣汁。', '“苦瓜别总炒鸡蛋了，像这样简单拌一拌，开胃好吃又下饭；酸酸辣辣，咸香脆爽，关键是还非常地简单快手，不信你也试试看~~~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (369, '凉拌木耳', '/api/img/2021081216287731677911958079.jpg', 53.00, '凉菜', '原料：木耳、蒜、葱、花生米、花生油、醋、生抽、蚝油、鸡精、盐。', '“黑木耳含蛋白质、脂肪、多糖和钙、磷、铁等元素以及胡萝卜素、维生素B1、维生素B2、烟酸等，还含磷脂、固醇等营养素。木耳营养丰富，被誉为“菌中之冠”。简单美味，健康营养，老少皆宜，开胃爽口快手菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (370, '香卤猪脷', '/api/img/2021081216287574329501958079.jpg', 14.00, '凉菜', '原料：猪脷、老卤汁、料酒。', '“咸香味美，爽口不油腻。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (371, '凉拌手撕杏鲍菇', '/api/img/2021081216287549522168197577.jpg', 48.00, '凉菜', '原料：杏鲍菇、小葱、大蒜、生抽、醋、盐、白糖、香油。', '“杏鲍菇口感肥嫩，略有弹性，其富含蛋白质和植物纤维，含大量人体必需氨基酸。杏鲍菇的吃法很多，炒烤煎拌均可。手撕杏鲍菇，没吃之前大多数人以为它嚼不动，其实不然。焯水后的手撕杏鲍菇，口感嫩滑，因为破口比较多，又是海绵状的组织，所以特别容易入味。拌一盘，开胃下饭，多放点蒜末或者蒜泥，提味还杀菌。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (372, '萝卜丝虾皮饼', '/api/img/2021100616335059888218197577.jpg', 46.00, '主食', '原料：白萝卜、虾皮、鸡蛋、手抓饼、酱油、蚝油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (373, '芝士厚蛋烧三明治', '/api/img/2021100616335006353778197577.jpg', 19.00, '主食', '原料：厚吐司、安佳淡味黄油、安佳芝士片、鸡蛋、牛奶、培根、欧芹、蛋黄酱、盐。', '“我最喜欢吃三明治，每天我都会拿它来当早餐，今天早餐做一个这样的三明治。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (374, '杏鲍菇鸡蛋馅素水饺', '/api/img/2021100516334348224401958079.jpg', 43.00, '主食', '原料：杏鲍菇、鸡蛋、白菜叶、生抽、香油、花椒粉、葱花、盐、鸡精、油。', '“杏鲍菇没有用盐沙一下，最后馅有点出水，也应先沙下再包。” 					', NULL, NULL, 1);
