INSERT INTO `foods` VALUES (379, '香肠蛋炒饭', '/api/img/2021100516334004829891958079.jpg', 24.00, '主食', '原料：剩米饭、香肠、鸡蛋、玉米粒、豌豆、蒜末、油、酱油。', '“剩米饭的华丽变身，添加自己喜欢的食材，剩饭也变得美味可口。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (380, '什锦炒饭', '/api/img/2021100416333439143008197577.jpg', 27.00, '主食', '原料：米饭、鸡蛋、洋葱、胡萝卜、豌豆、虾仁粒、盐、生抽、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (381, '鸡蛋韭菜盒子', '/api/img/2021100316332476377411958079.jpg', 33.00, '主食', '原料：普通面粉、韭菜、虾仁、鸡蛋、姜末、盐、鸡精、香油、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (382, '小白菜水饺', '/api/img/2021100316332273186561958079.jpg', 54.00, '主食', '原料：小油菜、胡萝卜、鸡蛋、面粉、食盐、生抽、蚝油、五香粉、香油。', '“小白菜的营养价值很高：它含有丰富的蛋白质、脂肪、钙和胡萝卜素；含蛋白质是大白菜的2倍，含钙成分也是大白菜的2倍，含胡萝卜素是大白菜的4倍。所以，多吃小白菜对人体有益……” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (383, '红枣紫米面窝窝头', '/api/img/2021100216331792717968197577.jpg', 43.00, '主食', '原料：稻米、小米、黑米、红枣、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (384, '酱油鸡腿焖饭', '/api/img/2021100216331692598508197577.jpg', 50.00, '主食', '原料：大鸡腿、大米、姜、八角、花椒、料酒、酱油、生抽、盐、水、小葱。', '“以前我经常做排骨焖饭、腊肉焖饭，但是都得把肉料炒一炒才成，今天我整了一锅酱油鸡腿焖饭，用了新方法特省事。鸡腿就用家常小料腌一腌，酱油的用量多一些吃着更美味。经过几个小时的腌制，咸味似乎都渗入骨头中了。把腌料汁倒入大米中，再把鸡腿放在上面同焖。嘿，还别说，这一锅米饭吸了鸡腿上的油脂，颗颗饱满油润，3块钱一斤的大米吃出了8块钱一斤的感觉。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (385, '豇豆麦饭', '/api/img/2021100216331530393968197577.jpg', 59.00, '主食', '原料：豇豆、玉米粉、面粉、五香粉、蒜泥、香油、陈醋、凉拌酱油。', '', NULL, NULL, 1);
