INSERT INTO `foods` VALUES (394, '海鲜蘑菇披萨🍕', '/api/img/2021092416324834822428197577.jpg', 54.00, '主食', '原料：高筋面粉、低筋面粉、酵母粉、糖、盐、油、牛奶、虾（蒜蓉腌过的虾）、鱿鱼圈、蘑菇、蘑菇、奶油、高汤、蒜瓣、黑椒盐。', '“制作全过程适合4岁以上宝宝操作😊。用自制的奶油蘑菇汁比番茄酱好吃太多了😋。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (395, '橄榄菜鸡腿菇炒饭', '/api/img/2021092416324613544118197577.jpg', 40.00, '主食', '原料：隔夜饭、鸡腿菇、鱼丸、鸡蛋、玉米粒、葱花、橄榄菜、盐。', '“剩饭大改造。简单的鸡蛋炒饭，用橄榄菜做主调料，风味便完全不一样了，小改变总有新发现。记得用爽口的菇类和肉类做配料，鸡腿菇+鱼丸，鲜冬菇+虾仁，首选搭配。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (396, '蘑菇汁猪扒饭', '/api/img/2021091816319679263168197577.jpg', 46.00, '主食', '原料：猪扒、鸡蛋、面包糠、面粉、蒜瓣、蘑菇、鲜奶油、黑椒盐、味淋、欧芹碎。', '“这菜的重点是自制蘑菇汁，材料简单，味道浓郁，煮汁过程（不包括切蘑菇和剥蒜）只需五分钟，某人说，下次直接煮蘑菇汁拌饭得了，可以不吃肉😓。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (397, '干南瓜馅包子', '/api/img/2021091616317701544431958079.jpg', 18.00, '主食', '原料：面粉、干南瓜、酵母、牛奶、白糖、蒜、花生油、盐、鸡精。', '“简单美味，健康营养，筋道有嚼头，百吃不腻，回味无穷。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (398, '鲜菇鸡汤米线🍜', '/api/img/2021091616317702083131958079.jpg', 57.00, '主食', '原料：米线、鸡腿菇、火锅肉片、鸡汤、鸡蛋、黑椒盐。', '“午餐时间，来锅热腾腾的过桥米线吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (399, '芹菜肉丝炒面', '/api/img/2021091616317703888251958079.jpg', 26.00, '主食', '原料：芹菜、肉丝、洋葱、小米椒、薄细面条、葱、花生油、花椒、辣椒、盐、生抽、老抽、鸡精、淀粉、白胡椒粉、姜、蒜。', '“微辣，油润鲜香，微微焦香的口感，好吃极了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (400, '河南特色蒸卤面', '/api/img/2021091616317562058591958079.jpg', 25.00, '主食', '原料：细面条、豇豆角、前腿肉、料酒、盐、老抽、五香粉、姜末。', '“河南有个特色面食，蒸卤面，是个很传统的面食，几乎家家都会做，虽然做法各有千秋，但总是风格一致，都是菜和面条混合蒸出来的。虽然做法不难，但很多人蒸出来面条会结团，或者不够筋道，那么不粘连又筋道的方法看我的分享吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (401, '香煎土豆丝饼', '/api/img/2021091616317562869721958079.jpg', 52.00, '主食', '原料：土豆、面粉、盐、鸡精、辣椒面、白胡椒粉、孜然粉、椒盐、食用油', '“咬一口香脆酥糯！咸香适口。隔壁小孩馋哭了，自家小孩吃不够₍₍ ᕕ⍢ᕗ⁾⁾简单方便，绝不失手！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (402, '一锅熟', '/api/img/2021091616317561211358197577.jpg', 14.00, '主食', '原料：香米、胡萝卜、牛排、午餐肉、青豆、玉米粒、西葫芦、生抽、老抽、蚝油、鸡精。', '“米饭、蔬菜、肉肉都在电饭锅里一起呈现。最适合忙碌没时间做饭的人们。也是儿时父亲经常做的饭。” 					', NULL, NULL, 1);
