INSERT INTO `foods` VALUES (406, '酱香牛肉焖饭', '/api/img/2021091416316266049058197577.jpg', 50.00, '主食', '原料：牛肉、洋葱、胡萝卜、土豆、葱、大米、青豆、黄肖氏酱香烧汁。', '“这是一道简单易做的快手焖饭，色香味俱全！吸饱汤汁的大块牛肉，软糯的土豆，Q弹的大米，香喷喷的一大碗，酱香浓郁，鲜美可口，入味十足，营养丰富！制作过程中无需再另外繁琐的加任何调味料了，一包黄肖氏酱香烧汁就搞定啦！厨房小白也可以做出超鲜的美味哦！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (407, '酱香牛肉焖饭', '/api/img/2021091416316266049058197577.jpg', 33.00, '主食', '原料：牛肉、洋葱、胡萝卜、土豆、葱、大米、青豆、黄肖氏酱香烧汁。', '“这是一道简单易做的快手焖饭，色香味俱全！吸饱汤汁的大块牛肉，软糯的土豆，Q弹的大米，香喷喷的一大碗，酱香浓郁，鲜美可口，入味十足，营养丰富！制作过程中无需再另外繁琐的加任何调味料了，一包黄肖氏酱香烧汁就搞定啦！厨房小白也可以做出超鲜的美味哦！” 					', NULL, NULL, 1);
