INSERT INTO `foods` VALUES (413, '番茄肉丸汤面', '/api/img/2021090916311719952961958079.jpg', 39.00, '主食', '原料：番茄、肉馅、挂面、青菜、葱末、姜末、鸡蛋、淀粉、盐、料酒、胡椒粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (414, '家常臊子面', '/api/img/2021090916311548461411958079.jpg', 58.00, '主食', '原料：五花肉、鸡蛋、木耳、黄花菜、豆腐、韭菜、土豆、胡萝卜、豆角、面条、葱、姜、蒜、盐、味精、十三香、老抽、酱油、料酒、辣椒面、老陈醋。', '“人到中年就应该放慢脚步，让自己的生活过得惬意一些，只有这样整个人的心情才能变得好起来。” 					', NULL, NULL, 1);
