INSERT INTO `foods` VALUES (417, '豆腐烧肉末拌面', '/api/img/2021090716310024185621958079.jpg', 37.00, '主食', '原料：猪肉、面条、豆腐、葱、青椒、姜、油、盐、料酒、生抽。', '“贴秋膘要循序渐进，荤素搭配最佳。豆腐搭配肉末烧制，动物蛋白与植物蛋白完美结合，营养丰富，养生保健。非常适合这个季节享用。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (418, '香辣炸酱面', '/api/img/2021090716309944104021958079.jpg', 43.00, '主食', '原料：面条、香菇、瘦肉末、线椒、纯净水、花生油、盐、老抽、生抽、料酒、五香粉、鸡精、淀粉。', '““面条”简单美味，香辣可口。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (419, '滑蛋炸虾饭 Ebi Katsu Don ', '/api/img/2021090716309926145728197577.jpg', 49.00, '主食', '原料：白饭、虾、洋葱、鸡蛋、面包糠、味淋、料酒、酱油。', '“半个洋葱，一碗白饭，两个鸡蛋，三只虾。自己动手做日料= 简单食材的复杂吃法😂” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (420, '茄汁大虾面', '/api/img/2021090716309853802361958079.jpg', 14.00, '主食', '原料：鲜虾、面条、杏鲍菇、葱、姜、油、盐、番茄酱、白糖。', '“面条是大爱。加了大虾的面条更是喜欢，海鲜贴秋膘妥妥的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (421, '南瓜馒头', '/api/img/2021090716309786661471958079.jpg', 37.00, '主食', '原料：面粉、酵母粉、南瓜泥、白糖。', '“这款【南瓜馒头】的做法和普通的馒头一样的，只是用南瓜泥代替水和面。用这种方法蒸制的馒头，同时也吸收了南瓜的营养成分，是一款老少皆宜的面食，口感喧软，色泽也很好看，颜值高好味道。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (422, '香菇牛肉饭', '/api/img/2021090616309221403578197577.jpg', 41.00, '主食', '原料：火锅肥牛片、鲜冬菇、姜、蒜、葱、黑椒盐。', '“快手小炒+白灼青菜+白饭。姜葱汁超级下饭🍚，感觉没牛肉都能吃两碗饭😂。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (423, '精肉豆角汤粉', '/api/img/2021090616309135242931958079.jpg', 53.00, '主食', '原料：熟肉、米粉、豇豆角、老干妈、食盐。', '“煮的精肉可以随时入菜，煮个粉啥啊的很方便。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (424, '西红柿鸡蛋拌面', '/api/img/2021090616309006735521958079.jpg', 42.00, '主食', '原料：面条、西红柿、鸡蛋、青菜、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (425, '黄瓜火鸡酱拌面', '/api/img/2021090516308479622961958079.jpg', 32.00, '主食', '原料：黄瓜、鸡蛋、面条、食用油、香葱、香油、食盐、料酒、火鸡面酱、原酿酱油。', '“#面条#最近秋老虎发威，天天高温，比三伏天还要闷热，总想吃点凉的食物；今天分享一道简单美味的黄瓜火鸡酱拌面给大家，清新不油腻， 香辣又过瘾；天热来一盘，营养丰富又美味，关键做法还非常简单；喜欢吃凉面的不妨试一试哟~~~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (426, '南瓜馒头 ', '/api/img/2021090516308341969151958079.jpg', 41.00, '主食', '原料：面粉、南瓜、水、酵母。', '“秋天是南瓜大量上市的季节，南瓜营养丰富，有益健康。但是买来一个大南瓜往往吃不完，只能分成块蒸熟冻起来。不过消耗它的方法还是很多哒，打汁、煮稀饭、烙南瓜饼、蒸馒头……下面开始干活咯……” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (427, '火腿蛋炒饭', '/api/img/2021090516308070187241958079.jpg', 52.00, '主食', '原料：米饭、鸡蛋、火腿、洋葱、胡萝卜、蒜、油、盐、生抽。', '“【火腿蛋炒饭】用的是金华火腿，这种火腿煸炒之后香味非常浓郁，用来炒饭味道好极了。这道炒饭里面火腿和鸡蛋是必须的，其它的配料可以按照自己的喜爱搭配，多一样少一样都不妨碍火腿的香味。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (428, '亚麻籽油炒饭', '/api/img/2021090416307228725358197577.jpg', 31.00, '主食', '原料：米饭、胡萝卜、榨菜、肉肠、豌豆粒、玉米粒、蒜、亚麻籽油、盐。', '“亚麻籽油不耐热，最适合用来拌凉菜。但因为它们味道不佳，如果和香油1：1混合，拌出来的菜就显得味道比较正常了。也可以和花生油、菜籽油等较耐热的油脂混合，用来做烹制菜肴。拌饺子馅、包子馅的时候，可以加一部分亚麻籽油。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (429, '胡萝卜素软包子', '/api/img/2021090316306516706111958079.jpg', 32.00, '主食', '原料：面粉、发酵粉、碱粉、糖、葱、地软、豆腐、胡萝卜、粉条、油、盐、五香粉、姜粉、蚝油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (430, '松茸杂酱面', '/api/img/2021090316306515991691958079.jpg', 53.00, '主食', '原料：面粉、水、猪肉、葱、松茸、黄瓜、甜面酱、油、盐、水、淀粉、酱油、五香粉、油泼辣子、醋。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (431, '南瓜莲蓉包（一次发酵）', '/api/img/2021090316306495335991958079.jpg', 16.00, '主食', '原料：普通面粉、熟南瓜泥、酵母、莲蓉馅。', '“用南瓜泥揉面，借助模具造型，外形立体美观，色泽金黄亮丽，面皮蓬松暄软，内馅香甜软糯，营养美味，老少皆宜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (432, '小葱虾仁蛋炒饭', '/api/img/2021083116303911872981958079.jpg', 60.00, '主食', '原料：剩米饭、鸡蛋、虾仁、火腿肠、小葱、油、盐、五香粉、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (433, '番茄炒蛋饭', '/api/img/2021083116303911184651958079.jpg', 30.00, '主食', '原料：番茄、鸡蛋、米饭、葱。', '“中午来个快手菜吧，随便弄一下。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (434, '西红柿鸡蛋煮馍', '/api/img/2021083116303809181611958079.jpg', 56.00, '主食', '原料：鸡蛋、西红柿、火腿肠、馒头、葱、姜、蒜、香菜、盐、白糖、味精、十三香、酱油。', '“简简单单的幸福生活，不过如此！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (435, '包菜鸡蛋饼', '/api/img/2021083016303144621091958079.jpg', 42.00, '主食', '原料：包菜、鸡蛋、小葱、食盐、胡椒粉。', '“少油少盐少糖是一种非常健康的饮食习惯，长期坚持有利于寿命的延长。现代人生活水平提高，生活节奏变化，运动时间相对较少，唯一可以轻松做出改变的健康方式就是低脂低盐的饮食了，可以有效预防和缓解三高，为健康添砖加瓦。今天分享一道我家经常食用的包菜鸡蛋饼，低盐低卡，制作简单，营养又好，赶快试一下吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (436, '海参炒生面', '/api/img/2021083016302947353991958079.jpg', 42.00, '主食', '原料：海参、猪肉、生面、洋葱、蒜苗、香菇、蚝油、酱油、胡椒粉、鸡精。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (437, '虾皮南瓜饼', '/api/img/2021083016302920716701958079.jpg', 19.00, '主食', '原料：南瓜、虾皮、鸡蛋、面粉、玉米油、盐、13香。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (438, '红枣花生乌米饭', '/api/img/2021083016302871219108197577.jpg', 31.00, '主食', '原料：糯米、枫叶汁、红枣、花生、糖。', '“我也不知道该怎么说自己，三月三的时候买了一大把枫叶，搞了好多枫叶汁，只为了煮点乌米饭。到现在冰箱里还有，我知道我会放久的，所以当时用的凉白开泡的，现在天气热也不想吃。这个还是蛮久之前做的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (439, '肉丝年糕汤', '/api/img/2021082916302027495678197577.jpg', 45.00, '主食', '原料：年糕、里脊肉、香葱、淀粉、稻米油、盐、胡椒粉、生抽。', '“简单营养的早餐，开启一天的新生活～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (440, '黄金火腿蛋炒饭', '/api/img/2021082816301601398011958079.jpg', 22.00, '主食', '原料：隔夜米饭、鸡蛋、火腿肠、盐。', '“以前不知道蛋炒饭还能这么做，金灿灿的，比一般蛋炒饭好吃多了。” 					', NULL, NULL, 1);
