INSERT INTO `foods` VALUES (443, '酸汤水饺', '/api/img/2021082816301399363141958079.jpg', 18.00, '主食', '原料：冷冻玉米猪肉水饺、紫菜、小葱、盐、鸡精、生抽、醋。', '“入秋时分，下班回家，身体乏累，煮一碗酸汤水饺，真的是方便又美味，还有家的味道。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (444, '生煎小油卷', '/api/img/2021082816301205846641958079.jpg', 31.00, '主食', '原料：面粉、水、酵母、盐、糖、广式甜肠、小葱、盐、油。', '“北方人爱吃面食，一个面团能玩出很多花样，油卷本身就是馒头的升级版本，用油或者油酥加小葱、盐、肉粒等材料做馅，味道更丰富，样貌也比大白馒头要好看一些。日常比较常见的油卷都是蒸出来的，因为“蒸”可以批量操作，只要蒸锅够大，一次性可以蒸很多。偶尔想换换口味的时候，我们也可以用生煎的方法做小油卷，今天分享的食谱就是直接生煎出来的小油卷，非常好吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (445, '葱花油饼', '/api/img/2021082816301162174281958079.jpg', 45.00, '主食', '原料：中筋面粉、小葱、盐、五香粉、油。', '“好吃又劲道，表皮酥脆，口感超级好，而且葱香味十足，每次吃到这个就想起小时候帮妈妈烧火，妈妈用地锅给我炕油饼，我就站在锅台前，做好一个，妈妈就会让我趁热先吃，那种拿在手里又烫手又烫嘴还吃的超级香的画面，突然感觉好幸福。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (446, '巨无霸汉堡', '/api/img/2021082716300715682718197577.jpg', 17.00, '主食', '原料：汉堡面包、切片芝士、烤牛肉排、煎蛋、生菜、牛肉、黄油、鸡蛋、蒜、洋葱、面粉、料酒、黑胡椒粉、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (447, '葱花肉松华夫饼', '/api/img/2021082716300515955108197577.jpg', 50.00, '主食', '原料：华夫饼粉、清水、鸡蛋、葱、肉松。', '“马上就要开学啦，孩子们的早餐又要安排上了。今天送上一款早餐王牌给大家参考 ，喜欢的可以复制一下哦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (448, '玉米白面饼', '/api/img/2021082716300450928591958079.jpg', 45.00, '主食', '原料：玉米粉、白面粉、酵母、韭菜、胡萝卜、油、鸡精、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (449, '窝蛋腊味饭', '/api/img/2021082716300340699551958079.jpg', 37.00, '主食', '原料：米、腊肠、腊肉、腊鸭、猪润肠、葱、豉油。', '“突然降温十几度，是时候来一锅杂锦腊味饭，腊肉，腊肠，润肠，腊鸭，加上鸡蛋，完美了！对于不喜生食的人，最理想的窝蛋应该是豆花状蛋白+9成熟刚凝固的蛋黄。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (450, '低脂生菜鸡蛋饼', '/api/img/2021082716300281484161958079.jpg', 20.00, '主食', '原料：鸡蛋、生菜、盐、油、黑芝麻酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (451, '炸馒头', '/api/img/2021082716300287818651958079.jpg', 12.00, '主食', '原料：馒头、炼乳。', '“这款是粤式炸馒头😊 1 馒头本身产自广州；2 馒头个头小巧，横截面有整齐的拉丝纹路；3 我的炸馒头正是回忆里广东茶楼的味道，得趁热蘸炼乳吃！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (452, '炸馒头', '/api/img/2021082716300287818651958079.jpg', 60.00, '主食', '原料：馒头、炼乳。', '“这款是粤式炸馒头😊 1 馒头本身产自广州；2 馒头个头小巧，横截面有整齐的拉丝纹路；3 我的炸馒头正是回忆里广东茶楼的味道，得趁热蘸炼乳吃！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (453, '里脊肉煎饺', '/api/img/2021082616299683829321958079.jpg', 19.00, '主食', '原料：饺子皮、里脊肉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (454, '玉米煎饼', '/api/img/2021082516298588936231958079.jpg', 14.00, '主食', '原料：糯米粉、面粉、玉米粒、水、糖。', '“在超市看到人家买甜玉米，想起来来我们家住的小表妹说想吃玉米，就买了两根回来。某天早上想起来以前在饭堂吃的两块钱一块的玉米煎饼，就想着做一下。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (455, '咖喱牛肉盖浇饭', '/api/img/2021082516298554763538197577.jpg', 26.00, '主食', '原料：牛肉、红萝卜、大米、土豆、洋葱、西兰花、咖喱块、料酒、酱油、盐、水、五香粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (456, '美味茴香煎饼', '/api/img/2021082516298551239411958079.jpg', 29.00, '主食', '原料：面粉、茴香、水、盐、五香粉、油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (457, '甜肠南瓜焖饭', '/api/img/2021082516298549535898197577.jpg', 12.00, '主食', '原料：大米、南瓜、甜肠、豆角、油、盐、蚝油、生抽。', '““秋天到，南瓜俏”这俗话说得真是好，秋季是南瓜丰收的季节，老妈菜园子里的几棵南瓜秧子还真结了不少，大大小小的南瓜家里最近都没有断过。各种南瓜美食也是越吃越爱呀。今天的南瓜焖饭一上桌，就被家人集体表扬了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (458, '简易三明治', '/api/img/2021082416297957912878197577.jpg', 40.00, '主食', '原料：吐司、火腿肉、鸡蛋、花生油、盐。', '“简单、易学。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (459, '绿豆胡萝卜婴儿饭', '/api/img/2021082416297831074971958079.jpg', 16.00, '主食', '原料：米、绿豆、胡萝卜、鸡胸脯。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (460, '培根披萨', '/api/img/2021082416297831733168197577.jpg', 13.00, '主食', '原料：披萨饼、培根、芝士、披萨酱、花生油。', '“喜欢就点个关注吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (461, '肉酱拌面', '/api/img/2021082416297689515661958079.jpg', 42.00, '主食', '原料：干扁面、肉酱、香菜、金蒜。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (462, '酸汤水饺', '/api/img/2021082416297691336191958079.jpg', 58.00, '主食', '原料：水饺、芝麻、花生碎、辣椒面、蒜末、醋、味极鲜、鸡精。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (463, '腊肉三明治', '/api/img/2021082316297263209558197577.jpg', 41.00, '主食', '原料：面包、腊肉、帕玛森奶酪、口蘑、西红柿、油、盐、黑胡椒碎。', '“女儿的脑洞很大，她把我中午买回家的腊肉夹进了面包片中，三明治的馅料除了腊肉，还加了一些帕玛森奶酪和口蘑，还魔幻般地煎了两块西红柿，把我蒸好的南瓜捣成了南瓜泥，再配上一杯我打好的热豆浆，这组合真是超好吃呀。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (464, ' 紫甘蓝饭团', '/api/img/2021082316297013177581958079.jpg', 54.00, '主食', '原料：米饭、紫甘蓝丝、黄瓜丁、午餐肉丁、白砂糖、味达美 清香米醋、沙拉酱、六月鲜 12克轻盐特级昆布酱油。', '“　　紫甘蓝含有丰富的维生素和矿物质，还有大量的纤维素，可以起到强身健体、降低胆固醇、促进肠蠕动的作用。紫甘蓝的紫色是花青素的颜色，花青素能减少紫外线对皮肤所带来的伤害，能抗辐射，能保护视力和提高免疫力，还能延缓血管细胞的衰老，保持血管的弹性，防止硬化，降低血压。　　花青素在碱性环境中会变成蓝绿色，在酸性环境中变成红色。为了得到漂亮的颜色，添加了一定的米醋。　　紫甘蓝营养这么好，颜色又好看，做成饭团，小朋友一定喜欢。而且制作简单，几分钟就做好了，再也不用担心上学来不及吃早饭了。　” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (465, '好看又好吃的红糖发糕，这样做一次就能做成功哦', '/api/img/2021082316296843451281958079.jpg', 47.00, '主食', '原料：红糖、热水、面粉、酵母、小苏打、红枣、植物油。', '“母亲比较喜欢吃发糕，之前她做发糕，要么太粘，要么没发起来，而这次按这个方子做出来的红糖发糕不黏牙，很好吃，喜欢的朋友可以做起来哦~这个分量至少是两人份的哦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (466, '大肉包子', '/api/img/2021082216296429545841958079.jpg', 27.00, '主食', '原料：面粉、猪肉馅、葱、姜、酵母、白糖、水、耗油、生抽、盐、鸡精、芝麻油。', '“传说中的大肉包子，这个点才出锅，那就当做夜宵吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (467, '黄金蛋炒饭', '/api/img/2021082216296427619941958079.jpg', 50.00, '主食', '原料：隔夜米饭、鸡蛋、青椒、火腿肠、盐。', '“解锁了新的炒饭技巧以后，我果断抛弃了酱油炒饭，甚至晚上闷饭的时候会多做一点点，隔夜之后，第二天就可以炒饭了。搭配蛋炒饭的配料，很多选择，有什么就炒什么。火腿，虾仁，玉米，青豆，胡萝卜，黄瓜，青椒，甜椒等等都可以。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (468, '豆角馍花麦饭', '/api/img/2021082216296368543261958079.jpg', 46.00, '主食', '原料：豇豆、手工蒸馍、面粉、大葱、大蒜、盐、十三香、鸡精、胡椒粉、辣椒面、花生油。', '“农家饭豆角馍花麦饭，，处处都是儿时的味道，香辣入味，好吃不厌。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (469, '芹菜猪肉饺子', '/api/img/2021082216296322396541958079.jpg', 38.00, '主食', '原料：猪腿肉、芹菜、盐、蚝油、鸡精、生抽。', '“下午休息，大宝说想吃芹菜猪肉馅的蒸饺，不要胡萝卜、不用葱、不要姜……好滴，满足🤗” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (470, '排骨焖饭', '/api/img/2021082216296203724261958079.jpg', 18.00, '主食', '原料：烧好的排骨、米、酱油、花椒油、黑椒汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (471, '番茄煎蛋拌面', '/api/img/2021082216296199245251958079.jpg', 12.00, '主食', '原料：番茄、鸡蛋、洋葱、蒜、小葱、盐、砂糖、蚝油、生抽、辣椒面、食用油。', '“夏天的小尾巴，不小心被我抓住。酸酸甜甜。配着加冰雪碧，啊！爽快！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (472, '卤肉包（烫面）', '/api/img/2021082216296161569561958079.jpg', 32.00, '主食', '原料：小麦面粉、玉米面、85度热水、盐、长茄子、卤肉、盐、生抽、五香粉、葱、生姜、蒜、蚝油、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (473, '玉米面发饼', '/api/img/2021082216296159644241958079.jpg', 28.00, '主食', '原料：玉米面、白面、牛奶、热水、发酵粉、白糖。', '“金黄酥香的玉米面发饼，松软香甜，脆而不硬，不用就菜能吃俩。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (474, '茄子青椒素包', '/api/img/2021081916293670461131958079.jpg', 13.00, '主食', '原料：面粉、糖、发酵粉、碱粉、青椒、茄子、大葱、粉条、油、盐、蚝油、五香粉、姜粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (475, '蒜香菠菜面', '/api/img/2021081916293665061631958079.jpg', 52.00, '主食', '原料：面粉、菠菜、蒜、葱、油、盐、辣椒粉、醋、酱油、芝麻。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (476, '羊肉洋葱拌面', '/api/img/2021081916293634346921958079.jpg', 38.00, '主食', '原料：中筋面粉、羊肉、洋葱、面粉用凉水、油、盐、酱油。', '“一年四季，即使天天吃面条也能不重样，手上的食材简单一搭配，保管让它有滋有味儿有营养。就像今天的这碗拌面，机器压的面条又快又滋润；拌面的“汆儿”用的羊肉炒洋葱。鲜美的羊肉、脆嫩的洋葱，羊肉和洋葱自身出来的汤汁浸润着面条，无需过多调料，盐和酱油就是最好的调味儿。这款面条一日三餐均可食用。午、晚餐不用多说了，如果是早餐吃，面条可以提前压或者切出来放冰箱冷藏或者冷冻，直接入沸水煮熟即可；当然喽，面条用挂面更省事。羊肉和洋葱提前一晚切出来，早上烧水煮面炒菜，用不了10分钟就能吃上，解馋补元气真带劲。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (477, '红糖核桃花卷', '/api/img/2021081916293512629921958079.jpg', 45.00, '主食', '原料：普通面粉、玉米面、核桃粉、红糖、玉米油、酵母粉、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (478, '照烧鸡排饭', '/api/img/2021081916293512099498197577.jpg', 52.00, '主食', '原料：鸡腿肉、料酒、生抽、胡椒粉、老抽、耗油、蜂蜜。', '“每次去菜市场到卖鸡肉的地方都会买好多，猪肉倒反而一般般 朋友前几天来家里吃饭买了鸡腿肉给小朋友做红烧鸡腿的 买了有点多所以留了三个拿来做鸡排饭 哈哈，都觉得自己太会过日子了不许笑我哈 主要还是贪吃 下了一段时间的雨终于消停一会儿了，天气还是很闷热 食欲也是忽高忽地” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (479, '荷香排骨饭', '/api/img/2021081916293368439458197577.jpg', 25.00, '主食', '原料：糯米、小米、子排、鲜荷叶、甜豆、生抽、蚝油、盐、味精、葱花。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (480, '番薯叶素肉包的详细教程，这样做，包子做的好看又好吃哦', '/api/img/2021081816292915186521958079.jpg', 46.00, '主食', '原料：温水、面粉、干酵母、番薯叶、香菇、素牛肉、糖、小苏打、素蚝油、盐、香油。', '“为同样素食的母亲，做了个早餐，她喜欢吃宣软的包子，就地取材，用当季的番薯叶和素牛肉做馅儿。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (481, '香菇菜包', '/api/img/2021081816292712871401958079.jpg', 22.00, '主食', '原料：上海青、干香菇、木耳、豆干、面粉、水、糖、酵母、泡打粉、猪油、盐、植物油、麻油。', '“素包子，吃素的看过来。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (482, '鸡蛋火腿炒饼丝', '/api/img/2021081816292647876658197577.jpg', 40.00, '主食', '原料：饼丝、鸡蛋、火腿肠、青椒、洋葱、茴子白、葱、姜、蒜、盐、味精、鸡精、十三香、南德调味料、料酒、酱油、蚝油。', '“有一种生活叫做为家人做饭然后看着他们吃光光！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (483, '三角饭团', '/api/img/2021081816292590505148197577.jpg', 38.00, '主食', '原料：米饭、五色糯米饭、玉米丁、胡萝卜丁、水煮鸡蛋、红肠片、火腿肠、沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (484, '鸡蛋炒饭', '/api/img/2021081816292589931628197577.jpg', 17.00, '主食', '原料：米饭、鸡蛋、洋葱、生菜、盐、油、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (485, '卤菜意大利面', '/api/img/2021081816292508508011958079.jpg', 32.00, '主食', '原料：意大利面、卤鸡中翅、卤蛋、卤鸭心、黑椒汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (486, '茄子花椒酱拌粉', '/api/img/2021081816292507417411958079.jpg', 30.00, '主食', '原料：米粉、熟精肉、大葱、茄子花椒肉酱。', '“有炒好的茄子花椒肉酱，还有煮好的精肉，所以煮个粉，放点配菜就是美味一餐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (487, '方瓜丝蛋皮饺', '/api/img/2021081716291844919531958079.jpg', 43.00, '主食', '原料：方瓜、面粉、鸡蛋、香菇、油、盐、葱、姜。', '“嫩嫩的方瓜最适合包饺子了，晨练结束路过菜场，看到大爷卖的方瓜，好新鲜，赶紧买个回家，心里已经想好要吃方瓜馅儿的饺子了😋。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (488, '红枣杂粮窝窝头', '/api/img/2021081716291831818958197577.jpg', 56.00, '主食', '原料：稻米、小米、黑米、红枣、清水。', '“这是家人都喜欢吃的窝头，平时都是婆婆做给我们吃，今天发现家里还有好多杂粮，想尽快消灭一下，所以想自己尝试一下，我是分别打的杂粮粉，然后混合在一起，感觉比例有点不太好，粘糯程度稍比婆婆做的逊色应该是大米面多一些，黑米小米少点，大米占三分之二吧，不过这个也很好吃呢！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (489, '葱油拌面', '/api/img/2021081716291798538491958079.jpg', 25.00, '主食', '原料：面条、香葱、熟白芝麻、油、生抽、老抽、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (490, '玉米面软饼', '/api/img/2021081716291794758341958079.jpg', 32.00, '主食', '原料：玉米面、白面、白糖、白芝麻、酵母粉、植物油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (491, '红油面皮', '/api/img/2021081716291677315081958079.jpg', 19.00, '主食', '原料：擀面皮、面筋、红萝卜、洋葱、青椒、辣椒红油、盐、醋、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (492, '什锦炒饭', '/api/img/2021081616291084672808197577.jpg', 36.00, '主食', '原料：米饭、胡萝卜、洋葱、豌豆、鸡蛋、玉米粒、盐、生抽、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (493, '炖土豆意大利面', '/api/img/2021081616290852357231958079.jpg', 36.00, '主食', '原料：意大利面、炖土豆、黑椒汁', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (494, '烤紫山药馍片', '/api/img/2021100616335061908548197577.jpg', 37.00, '小吃', '原料：面粉、鸡蛋、紫山药、食用油。', '“只要是烤馍片，就是给我妈做的。她用来当零食，每次快吃完了，就说“我的烤馍快没有了！”我就赶紧给做新的。这次我用紫山药来做的，保留山药颗粒，好看又好吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (495, '萝卜丝虾皮饼', '/api/img/2021100616335059888218197577.jpg', 41.00, '小吃', '原料：白萝卜、虾皮、鸡蛋、手抓饼、酱油、蚝油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (496, '阿胶糕', '/api/img/2021100616335007368128197577.jpg', 48.00, '小吃', '原料：阿胶块、黑芝麻、枸杞、红枣、老冰糖、核桃。', '“秋天补气补血，美容养颜，手脚冰冷。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (497, '烤冷面', '/api/img/2021100616334973551738197577.jpg', 33.00, '小吃', '原料：冷面饼、鸡蛋、火腿肠、香菜、黑芝麻、烤冷面酱、醋、烧烤料、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (498, '烤赤小豆', '/api/img/2021100616334849333798197577.jpg', 24.00, '小吃', '原料：赤小豆、无、无。', '“为了搭配烤的薏米，把赤小豆也烤了。即可以直接煮汤，也可以打成粉。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (499, '红糖冰粉', '/api/img/2021100516334354108981958079.jpg', 34.00, '小吃', '原料：冰粉粉、水、红糖、花生、瓜子碎。', '“香甜适口，降噪润喉！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (500, '酥炸鱿鱼圈- 气泡水面糊版', '/api/img/2021100516334090971481958079.jpg', 15.00, '小吃', '原料：鱿鱼圈、面粉、蛋白、气泡水、蒜盐、盐。', '“前几天炸鱿鱼圈时用蛋白拌面糊，已经非常好吃，这次尝试蛋白+气泡水拌面糊，结果只能～ 更加好吃，大人小孩吃不停手😋。只加蛋白的面糊，外壳酥脆带硬；加入气泡水后，外壳酥脆而蓬松，喜欢的朋友一定要试试。气泡水就是苏打水，我用的是不含糖和任何味道的。不过，用其他有气泡的饮料代替也行，例如啤酒，可乐，芬达等，很容易找到。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (501, '泡椒凤爪', '/api/img/2021100516334027456391958079.jpg', 20.00, '小吃', '原料：鸡爪、泡椒、葱、姜、胡萝卜、泡椒汁。', '“泡椒凤爪是四川的有名的一道小吃，既可以当下酒菜，也可以当日常的凉菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (502, '胶钱罗', '/api/img/2021100516333998721901958079.jpg', 60.00, '小吃', '原料：糯米粉、开水、芝麻花生、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (503, '脆皮油条', '/api/img/2021100516333986601331958079.jpg', 42.00, '小吃', '原料：面粉、鸡蛋、无铝泡打粉、酵母粉、食用油、食用盐、白糖。', '“百里不同风，千里不同俗。每个地方都有着自己独具特色的小吃，但是油条在许多地方都有，这是一个经久不衰的美味小吃，原因很简单，赶时间方便拿在手上可以边走边吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (504, '羊肉胡卜', '/api/img/2021100316332474326951958079.jpg', 53.00, '小吃', '原料：饼丝、羊肉、羊汤、葱、姜、蒜、香菜、黄花菜、西红柿、盐、味精、十三香、羊油辣椒。', '“平淡是白开水，品尝之时无味，平淡之中却见真情真意，平淡之中是一种另类的幸福所归。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (505, '泡椒鸡爪', '/api/img/2021100216331690831088197577.jpg', 17.00, '小吃', '原料：鸡爪、清水、食盐、葱、酒菠萝、卤肉料、姜、纯净水、食盐、鸡精、白醋、泡椒、小米辣。', '“昨晚少爷他爹说嘴巴寂寞，想吃东西。给他洗好了水果又不想吃，我就给做了这个泡椒鸡爪。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (506, '青菜芋泥糊', '/api/img/2021100216331395924278197577.jpg', 13.00, '小吃', '原料：芋头、菜心、大蒜、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (507, '黑燕麦葛根粉', '/api/img/2021100216331394875898197577.jpg', 48.00, '小吃', '原料：黑燕麦、葛根粉、芝麻、枸杞、水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (508, '酥炸鱿鱼圈 — 蛋白面糊版', '/api/img/2021100116330917273641958079.jpg', 31.00, '小吃', '原料：鱿鱼圈、面粉、蛋白、海苔碎、盐、盐。', '“软软糯糯的鱿鱼圈🦑😋，裹上蛋白面糊，从第一个到最后一个，都是酥酥脆脆的口感，表层脆壳不会因为被压着，或者冷却而软化，有点像刚炸好的天妇罗，但比娇气的天妇罗（表层一降温就软塌下来）更香更脆。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (509, '原味烤油板栗', '/api/img/2021100116330786252181958079.jpg', 55.00, '小吃', '原料：油板栗。', '“这种油板栗本身就甜，热量很高，所以直接烤了原味的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (510, '水果奶油可丽饼', '/api/img/2021093016330077764098197577.jpg', 37.00, '小吃', '原料：JUST Egg 植物蛋、清水、玉米油、细砂糖、低筋面粉、淀粉、奶油、青提葡萄。', '“现在越来越多的朋友和我一样，每月会选择几天茹素。除了传统的蛋奶素，也有许多朋友选择纯素。最近早上路过这家永康路口的煎饼小屋照例买早点，发现他们家还提供纯植物造的植物蛋煎饼，居然价格和普通煎饼一样。之前就听说过，这次不仅尝试了一下，而且今日份食谱也是用同款植物蛋做的哦，就是这份颜值在线的可丽饼🌯，男女老少皆食得！” 					', NULL, NULL, 1);
