INSERT INTO `foods` VALUES (522, '炸鱼薯条Fish & Chips', '/api/img/2021092316324005147081958079.jpg', 46.00, '小吃', '原料：面粉、啤酒、蒜盐/黑椒盐、巴沙鱼块/龙利鱼柳、土豆、蒜盐/黑椒盐、盐。', '“在“Fish and chips”的世界里，fish和chips的做法有很多种，这里介绍的是battered fish cocktails + wedges chips。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (523, '孜然火腿土豆丁', '/api/img/2021092316323878078538197577.jpg', 28.00, '小吃', '原料：土豆、火腿肠、白芝麻、辣椒面、孜然粉、生抽、盐、小葱。', '“孜然火腿土豆丁不管是大朋友还是小朋友都喜欢吃，但就是做的不好吃，做不出路边摊的那种味道，今天十天美食就教你做，比路边摊还好吃的孜然火腿土豆丁” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (524, '原味炸薯角 - 红薯版', '/api/img/2021092316323753688951958079.jpg', 22.00, '小吃', '原料：红薯/番薯、面粉、盐。', '“微风轻拂的午后，晒着太阳，泡着清茶，再配个炸薯角，开启愉快的下午茶时间。一大盘软糯香甜的炸红薯，全部制作过程只需十分钟！方法跟炸土豆一样，￼喜甜的朋友不容错过。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (525, '炸鸡柳', '/api/img/2021092316323752525268197577.jpg', 29.00, '小吃', '原料：鸡胸肉、黄面包糠、鸡蛋、葱段、生抽、蚝油、盐、胡椒粉、料酒、玉米淀粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (526, '米姜', '/api/img/2021092316323750968268197577.jpg', 44.00, '小吃', '原料：姜、冰糖、水、糖。', '“秋季一天两片姜，手脚不再凉，暖胃驱寒。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (527, '炸鸡排', '/api/img/2021092316323678287611958079.jpg', 54.00, '小吃', '原料：鸡胸肉、鸡蛋液、淀粉、奥尔良料、面包糠。', '“所有的孩子都喜欢鸡排饭，但外面卖油炸食品实在是不放心，自己动手超级省事又健康。” 					', NULL, NULL, 1);
