INSERT INTO `foods` VALUES (529, '家庭版糖炒栗子', '/api/img/2021092216323053175941958079.jpg', 43.00, '小吃', '原料：板栗、糖、食用油、水。', '“只需要一口锅～小白也能轻松学会的家庭版糖炒栗子～      板栗中含有大量的不饱和脂肪酸以及维生素，可以促进胃肠道内物质的消化与吸收，能起到健脾养胃的功效，还可以抑制血压的升高。       板栗中含有丰富的核黄素，可以有效的预防口腔溃疡以及口舌疮等疾病。       此外板栗中还含有大量的碳水化合物，进入人体内可以转化为热能，可以加快脂肪的代谢，能起到健脾养胃的功效。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (530, '驴打滚', '/api/img/2021092116322059101301958079.jpg', 13.00, '小吃', '原料：糯米粉、牛奶、植物油、紫薯泥、黄豆粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (531, '家常酸辣粉', '/api/img/2021091716318554934888197577.jpg', 55.00, '小吃', '原料：干番薯粉条、花主米、榨菜、小白菜、香菜、辣椒粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (532, '酥炸虾丸', '/api/img/2021091516317122096081958079.jpg', 32.00, '小吃', '原料：虾肉、鱼滑、面包糠、鸡蛋、蚝油、麻油、胡椒粉。', '“外面卖的虾丸，会掺杂各种粉以丰盈体积，我有时会怀疑自己吃的是假虾丸。想吃上真材实料，最简单的方法是自己动手。酥酥脆脆的外壳，弹性十足的肉馅，厚实的质感，让人好满足。这里肉馅的成分是虾肉和鱼肉，如果你全部用虾肉也是没问题的，一样的做法。我刀功不好，剁不出顺滑的虾滑，所以要靠机器打的鱼滑增加丸子弹性。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (533, '美味大赏之冰糖草莓', '/api/img/2021091516316866572191958079.jpg', 28.00, '小吃', '原料：草莓、砂糖、清水、竹签。', '', NULL, NULL, 1);
