INSERT INTO `foods` VALUES (535, '炒河粉', '/api/img/2021091216314106376578197577.jpg', 43.00, '小吃', '原料：宽河粉、绿豆芽、牛肉、胡萝卜丝、海帶丝、生抽、腐乳、蚝油。', '“米粉在我们这里几乎每天都会吃，一般在早上吃的多，但有时候也会是一日三餐都可以，有各种吃法，只要你喜欢，怎么吃都不厌😋” 					', NULL, NULL, 1);
