INSERT INTO `foods` VALUES (549, '甜辣混酱猪肠粉', '/api/img/2021090516308061721781958079.jpg', 58.00, '小吃', '原料：猪肠粉、白芝麻、温水、韩式辣椒酱、番茄酱、蚝油、白糖、鸡精、白芝麻、温水。', '“这道混酱猪肠粉和港式的混酱猪肠粉不一样，这道猪肠粉必不可少的是韩式辣椒酱，甜辣的口感，会让人忍不住多吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (550, '孜然土豆', '/api/img/2021090216305606216218197577.jpg', 23.00, '小吃', '原料：土豆、烤肠、洋葱、孜然粒、孜然粉、辣椒面、盐、白芝麻、生抽、色拉油、小葱花。', '“喜欢吃土豆的同学们，一定要试试这道菜，烧烤味浓郁，咸香下饭” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (551, '素菜春卷', '/api/img/2021082916302438873848197577.jpg', 17.00, '小吃', '原料：春卷皮、笋干、木耳、韭菜、葱、花生油、盐、鸡精、蚝油、生抽。', '“今天虽然不是过节，但是突然很想吃春卷，那就买。春卷皮一斤10元，买了一斤。平常节日的时候买，一斤要二十几元。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (552, '果冻', '/api/img/2021082916302326069608197577.jpg', 10.00, '小吃', '原料：白凉粉、凉白开、热水。', '“家里正好买了一包白凉粉，我就准备开始做果冻啦。” 					', NULL, NULL, 1);
