INSERT INTO `foods` VALUES (557, '包浆豆腐', '/api/img/2021082816301503088711958079.jpg', 55.00, '小吃', '原料：包浆豆腐、植物油、辣椒蘸料。', '“看到包浆豆腐一口爆浆，感觉很好吃，买了包也来尝试做一下，这次做的是烤的和煎的两种做法。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (558, '虾扯蛋', '/api/img/2021082816301492881831958079.jpg', 15.00, '小吃', '原料：低粉、鸡蛋、泡打粉、酱油、味啉、水、鹌鹑蛋、虾、葱花、木鱼花、海苔碎、番茄酱、千岛酱、沙拉酱。', '“谁说这是瞎扯淡？谁又敢说这不是虾扯蛋？哈哈……中国的文字一准儿把老外弄懵😂😜” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (559, '懒人煎饼', '/api/img/2021082816301444621421958079.jpg', 34.00, '小吃', '原料：手抓饼、鸡蛋、红肠、盐。', '“下雨天好想吃零食哦，把家里现有的食物加工一下吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (560, '黄金五黑丸', '/api/img/2021082716300690056388197577.jpg', 19.00, '小吃', '原料：黑豆、黑芝麻、黑米、黑枸杞、桑葚干、熟核桃仁、红枣肉、奇亚籽、龙舌兰糖浆。', '“近期几个月，压力有点大，有时候还熬夜，头发掉的厉害。就想着赶紧给自己补一补。这个配方是从一位达人那里学来的，她吃了之后，头发是肉眼可见的变黑变多，自己也来做个吃吃，坚持吃下去一定会有效果哒。感兴趣的朋友可以安排起来呀。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (561, '固元膏', '/api/img/2021082616299823797131958079.jpg', 16.00, '小吃', '原料：熟核桃仁、熟黑芝麻、枸杞、去核红枣、红糖、水、麦芽糖。', '“秋季宜进补，红枣、核桃、黑芝麻和枸杞四大原料组成的固元膏很适合秋冬吃呢。这固元膏甜而不腻，口感偏软，又香又好吃，清晨或下午来上两片，既解馋又补充能量呀！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (562, '玫瑰解郁丸', '/api/img/2021082616299474109908197577.jpg', 60.00, '小吃', '原料：山楂、枸杞、兰州百合、熟白芝麻、去核红枣、玫瑰花瓣、龙舌兰糖浆。', '“解郁丸的主要功能就在于疏导郁结的肝气，行血化瘀，不过东西再好，也只能帮助身体运转气血，治标不治本。我们首先来想想为什么会肝气郁结呢？其实主要原因是情绪不佳，说白了，就是生气，不开心。所以保持天天好心情，才能不再给身体添乱哦！再配上一天几颗的玫瑰解郁丸，一定越变越美呀！试吃一下，酸酸甜甜还有玫瑰的花香，挺好吃的呢！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (563, '红油米线', '/api/img/2021082516298605488591958079.jpg', 32.00, '小吃', '原料：米线、海带、青菜、豆皮、豆泡、绿豆芽、豆腐干、花椒酱、胡椒粉、盐、酱油、郫县豆瓣、花椒粉、葱花、香菜。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (564, '鸡蛋肉卷可丽饼', '/api/img/2021082416297831119158197577.jpg', 14.00, '小吃', '原料：小麦粉、豆本豆、叉烧、鸡蛋、黑椒汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (565, '脆皮年糕，带我重回校园时光', '/api/img/2021082416297734525638197577.jpg', 46.00, '小吃', '原料：年糕、植物油、白芝麻、素肉松、甜面酱、番茄酱、糖浆、孜然粉。', '“小时候在学校上学，有时候就会买脆皮年糕吃，当时觉得好好吃，酱料撒料都嫌不够，现在自己做来吃，酱料和撒料都自由了。真的很好吃呢！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (566, '烤苕皮', '/api/img/2021082416297708726678197577.jpg', 25.00, '小吃', '原料：苕皮、葱花，香菜末、洋葱末、火腿丁、榨菜（酸萝卜，酸豆角）、蘸水辣椒粉、蒜蓉酱、烤串料。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (567, '章鱼烧—鲜虾小丸子', '/api/img/2021082416297687966071958079.jpg', 22.00, '小吃', '原料：面粉、三文鱼、虾、鸡蛋、章鱼烧酱、木鱼花、胡萝卜、洋葱、盐、橄榄油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (568, '炒年糕这样炒，入味又好吃呀！', '/api/img/2021082316297089198648197577.jpg', 31.00, '小吃', '原料：毛毛菜、胡萝卜、素肉排、豆芽、年糕、生抽、盐、素蚝油。', '“上次上传的炒年糕视频，播放量点赞率都很高哦，看来大家都很喜欢这炒年糕呀！那就把做法整理一下成菜谱啦~小时候就是喜欢吃街边炒的年糕，可我母亲就是炒不出来这种味道，这次倒是我自己炒出来了，很棒呀！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (569, '食光有约之洋芋螃蟹', '/api/img/2021082316297005334608197577.jpg', 23.00, '小吃', '原料：洋芋、鸡蛋、面粉、白胡椒、盐、单山蘸水。', '“因其外形貌似螃蟹而得其美名。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (570, '食光有约之炸鸡脯丸', '/api/img/2021082316296966818028197577.jpg', 49.00, '小吃', '原料：鸡脯肉、蛋白、干淀粉、白面包糠、番茄酱、盐', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (571, '奶香南瓜籽', '/api/img/2021082316296823838278197577.jpg', 54.00, '小吃', '原料：南瓜籽、黄油、白糖。', '“废物利用，最近吃南瓜比较多，就把南瓜子收集起来了，南瓜子富含维生素，氨基酸，矿物质，还有降压的作用。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (572, '润肺止咳的秋梨膏呀！', '/api/img/2021082216296238977701958079.jpg', 50.00, '小吃', '原料：梨、红枣、生姜、罗汉果、川贝、黄冰糖、水。', '“过了立秋，就发现天气有点干燥，嗓子容易干咳，听说秋梨膏润肺效果特别好，喝了不容易咳嗽，昨天也花了点时间，熬了个秋梨膏。 清甜的梨子味道，还不错哦，喜欢的朋友也可以安排起来哦，秋冬也要好好爱自己呀。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (573, '红糖椰蓉南瓜糍粑', '/api/img/2021082216296237463821958079.jpg', 50.00, '小吃', '原料：糯米粉、南瓜块、白糖、油、红糖、黑芝麻、水。', '“其实是又做了南瓜糯米糍，但为了把糯米粉全用上就加多了，做好放冰箱第二天再吃就硬了，于是用它为原材料切片煎了做了红糖糍粑。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (574, '油酥豌豆', '/api/img/2021082216296152801921958079.jpg', 32.00, '小吃', '原料：豌豆、油、盐、糖、辣椒粉。', '“从小就喜欢的油酥豌豆，每次出去吃火锅豆花酸辣粉都少不了它～今天把做法分享给大家” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (575, '奶油爆米花', '/api/img/2021082216295980532581958079.jpg', 16.00, '小吃', '原料：专用玉米粒、黄油、白砂糖、色拉油。', '“爆米花和电影更配哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (576, '芝士泡菜年糕', '/api/img/2021082116295427195858197577.jpg', 26.00, '小吃', '原料：年糕、辣白菜、马苏里拉芝士、韩式辣酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (577, '脆皮香蕉', '/api/img/2021082116295100405808197577.jpg', 19.00, '小吃', '原料：香蕉、鸡蛋、红薯淀粉、面包糠。', '“油脂，可能不太健康，但是，真的很快乐！脆皮香蕉，香甜软糯又酥脆，超级快乐！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (578, '无油少糖土豆脆片', '/api/img/2021082016294232263468197577.jpg', 15.00, '小吃', '原料：土豆泥、红薯粥、胡萝卜末、淀粉、黑胡椒粉、蜂蜜、糖、盐。', '“　　喜欢吃薯片吗？似乎很少有人能抵挡这一著名的垃圾食品的魅力。今天给大家推荐一款无油少糖的薯片做法，又松又脆，甜、咸口味都可以，哪怕不调味都能让你咔嚓咔嚓停不下来！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (579, '上海名菜四喜烤麸', '/api/img/2021081916293819631478197577.jpg', 14.00, '小吃', '原料：烤麸(水发挤干后)、黑木耳(干)、黄花菜(干)、花生米、油、六月鲜 特级酱油、六月鲜 红烧酱油、白砂糖。', '“　　四喜烤麸属于上海本帮菜，有着上海菜的显著特色——浓油赤酱，成菜酱香浓郁，油润绵软又有弹性，鲜咸中透着甜蜜，配菜木耳脆、花生香，口感丰富，十分下饭。所谓四喜，是指烤麸、木耳、黄花菜、花生米四种食材。但又有一种说法：四喜烤麸原来叫做四鲜烤麸，在上海话中“鲜”与“喜”音相近，为讨口彩就叫四喜烤麸了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (580, '关东煮（家庭版）', '/api/img/2021081916293764131168197577.jpg', 33.00, '小吃', '原料：各种丸子、豚骨拉面、海带结、菇类、海米、盐。', '“夏天马上要结束了。开启空调。第一锅关东煮安排！家里买的关东煮丸子套餐拆开了才发现没有料包。但是菜都切好了等着下锅。机智的我大胆的试了一下用豚骨拉面的料包。简直绝了！推荐小伙伴们。省钱又快手！简单不出错。（小声说:还好省钱呀。）快来试试吧？” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (581, '炒腰果', '/api/img/2021081916293638989678197577.jpg', 18.00, '小吃', '原料：腰果、花生油、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (582, '红枣核桃糕', '/api/img/2021081916293444739221958079.jpg', 11.00, '小吃', '原料：去皮后的红枣肉、核桃仁、麦芽糖、黄油、木薯粉、水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (583, '奶黄包', '/api/img/2021081816292946655131958079.jpg', 45.00, '小吃', '原料：面粉、奶黄馅、酵母、水、白糖。', '“白天上班出门就是一天，趁着晚上有时间给小宝做几个奶黄包，挑食的娃，不吃菜包，肉包……只喜欢奶黄的味道” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (584, '凉粉果冻', '/api/img/2021081816292800026408197577.jpg', 26.00, '小吃', '原料：白凉粉、清水、糖、牛奶。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (585, '卤菜肠粉', '/api/img/2021081816292508935161958079.jpg', 32.00, '小吃', '原料：米、卤菜、清水、酱油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (586, '水煮花生', '/api/img/2021081716291920708871958079.jpg', 11.00, '小吃', '原料：花生、盐、桂皮、香叶、花椒、八角。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (587, '啤酒卤蛋', '/api/img/2021081716291857739031958079.jpg', 36.00, '小吃', '原料：鸡蛋、啤酒、盐、老抽、白糖。', '“早餐爱好者，喷香Q弹，超级美味好吃。” 					', NULL, NULL, 1);
