INSERT INTO `foods` VALUES (589, '卤菜', '/api/img/2021081716291779967298197577.jpg', 38.00, '小吃', '原料：鸭脚掌、鸡中翅、鸭心、五香老卤调料', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (590, '空气炸锅—-香蕉鸡蛋手抓饼', '/api/img/2021081416289311215331958079.jpg', 53.00, '小吃', '原料：手抓饼、香蕉、鸡蛋、黑芝麻、白芝麻。', '“可以当早餐也可以做甜品，又可以做零食，每次做，都是儿子一个人吃完😄” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (591, '椰香薄脆', '/api/img/2021081416289055451911958079.jpg', 55.00, '小吃', '原料：新椰城椰浆、低筋面粉、蛋黄、椰子油、无铝泡打粉、绵白糖、盐。', '“　　薄脆是我做得比较多的小零食，老老小小都喜欢。以前做薄脆大多用米饭做，这次想用饼干方子来做。因着添加了椰浆，我就用了椰子油，双倍椰香，香死个人……　　这个配方有水分（椰浆），油的量也不多，想要做出酥脆的口感，得加点儿膨松剂，所以配方中有2克无铝泡打粉。泡打粉有害就在铝上，无铝的泡打粉是不要紧的，如果你介意添加剂，那可以去我的主页或公众号“眉记”找找《果蔬脆片》、《荞麦脆片》这两篇食谱。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (592, '油条鸡蛋卷饼', '/api/img/2021081316288447344788197577.jpg', 17.00, '小吃', '原料：小油条、鸡蛋、面粉、甜面酱、葱、油、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (593, '茶叶蛋', '/api/img/2021081316288326455238197577.jpg', 49.00, '小吃', '原料：鸡蛋、鹌鹑蛋、香味、八角、良姜、桂皮、茶叶、食盐、老抽、生抽。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (594, '网红青团<法帅蒸汽烤箱式>', '/api/img/2019040115541123656029724956.jpg', 42.00, '小吃', '原料：糯米粉、澄粉、艾草粉、热水、咸蛋黄、肉松、糖、油、料酒。', '“要问什么食物最能代表江南的春天那必须是青团就像冬至要喝冬酿酒腊八要吃腊八粥一样清明自然少不了青团将最嫩的艾草捣烂为汁和糯米粉揉团捏壳再包裹豆沙等馅料蒸熟后抹一层油油绿如玉，锃亮一新，软糯Q弹” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (595, '糖烤栗子【法帅蒸汽烤箱版】', '/api/img/20190323155333348536910138013.jpg', 17.00, '小吃', '原料：板栗、白糖、水、玉米油、法帅蒸汽烤箱。', '“在这个四季长春的南方唯一能感受到秋天的气息则是这满街飘香的糖烤栗子轻易剥掉外壳香甜酥松秋天的黄昏，一杯红茶配栗子云淡风轻，轻松自由” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (596, 'Q弹糯米糍【法帅蒸汽烤箱版】', '/api/img/20190323155333268355210138013.jpg', 57.00, '小吃', '原料：糯米粉、常温水、椰浆、糖。', '“小时候童年很简单没有手机，没有电脑，简简单单打弹珠、翻花绳、小人书……5毛钱买份糯米糍小伙伴一起吃却出奇的开心与美好” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (597, '嫩滑蒸水蛋【法帅蒸汽烤箱式】', '/api/img/20190323155332235283910138013.jpg', 17.00, '小吃', '原料：鸡蛋、温水、油、盐、酱油、葱花。', '“欧洲贵族宫廷风格台式蒸汽烤箱远离油烟 静享清新厨房上下背部发热管加热风循环轻松搞定米其林星级大餐” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (598, '自制豆腐脑', '/api/img/2021081316288213143738197577.jpg', 30.00, '小吃', '原料：黄豆、豆腐内脂、骨头汤、香菜、紫菜、清水、辣椒油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (599, '孜然小土豆', '/api/img/2021081316288187598498197577.jpg', 35.00, '小吃', '原料：小土豆、植物油、盐、椒盐、孜然粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (600, '剩米饭的花样早餐吃法，米饭鸡蛋饼', '/api/img/2021081216287607017768197577.jpg', 14.00, '小吃', '原料：剩米饭、鸡蛋、洋葱、胡萝卜、食用油、盐、鸡精、胡椒粉。', '“快速消灭剩米饭的新吃法，健康营养还香气四溢，非常好吃的米饭鸡蛋饼。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (601, '牛肉米线', '/api/img/2021081216287463884598197577.jpg', 27.00, '小吃', '原料：米线、熟牛肉、韭菜、豆皮、生菜。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (602, '红薯饼', '/api/img/2021081216287463518398197577.jpg', 34.00, '小吃', '原料：红薯、面粉、玉米淀粉、白糖、清水、花生油。', '“现在正是早熟红薯成熟的季节，嫂子昨天从老家来给我带了一箱，每次都是蒸着吃，煮着吃，拔丝红薯也吃了好多次，今天就给它想个别样吃法，红薯饼，没想到还真过瘾，外皮酥脆，内心甜糯。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (603, '香酥鸡排', '/api/img/2021081216287344826581958079.jpg', 54.00, '小吃', '原料：鸡胸肉、鸡蛋、玉米淀粉、面包糠、面粉、油、盐、料酒、酱油、五香粉、姜粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (604, '冰豆花', '/api/img/2021081116286884539598197577.jpg', 41.00, '小吃', '原料：黄豆、内脂、炼奶、清水、红糖、白糖。', '“去年做了一款冰醉豆花，今年又带来一款冰豆花，用酸奶杯盛装，每人一杯，巴适。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (605, '香酥鸡蛋卷', '/api/img/2021081116286684571031958079.jpg', 21.00, '小吃', '原料：低筋粉、蛋黄、鸡蛋、核桃油、柠檬、白糖、盐。', '“孩子都爱吃的鸡蛋卷，在家用一口平底锅就可以做，而且我用了核桃油，给孩子吃更放心。没有蛋卷机又想做蛋卷的就试试这个方法吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (606, '油饼', '/api/img/2021081116286463361451958079.jpg', 21.00, '小吃', '原料：面粉、温水、泡打粉、发酵粉、白糖、香豆子粉、盐、玉米油、花生油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (607, '香肠藕丁土豆饼', '/api/img/2021080616282140643058197577.jpg', 33.00, '小吃', '原料：土豆泥粉、开水、莲藕、香肠、洋葱、沙拉酱、目鱼花、章鱼烧酱、盐、黑胡椒。', '“买了一大包的土豆泥粉，本来是想着减肥吃的。做了几次土豆泥，这次换个口味。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (608, '椒盐土豆', '/api/img/2021080616282137827608197577.jpg', 32.00, '小吃', '原料：土豆、黑芝麻、椒盐、孜然粉、烧烤撒粉。', '“去湖北吃了椒盐土豆，回来后一直回味，今天也尝试一下。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (609, '钵钵鸡', '/api/img/2021080516281713703708197577.jpg', 42.00, '小吃', '原料：豆腐、生菜、金针菇、西兰花、香菇菜丸、包心肉丸、包心鱼丸、撒尿牛丸、钵钵鸡香辣菌菇素菜底锅。', '“夏天，想吃火锅又怕热怎么办？好办啊，今天就跟大家分享一款可以凉吃的火锅~钵钵鸡；钵钵鸡是什么？钵钵鸡就是冷吃火锅，想吃钵钵鸡不用去四川，有了它，在家就能做；简单方便，川味十足，开胃还好吃，喜欢的朋友不娘试试吧~~~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (610, '拉丝紫薯饼', '/api/img/2021080516281442023448197577.jpg', 27.00, '小吃', '原料：紫薯、马苏里拉奶酪芝士、糯米粉、白糖、油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (611, '蔬菜蛋煎饼', '/api/img/2021080516281410572301958079.jpg', 59.00, '小吃', '原料：中筋面粉、鸡蛋、胡萝卜丝、白菜丝、虾米、食用油、水、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (612, '豆沙酥饼', '/api/img/2021080516281300314741958079.jpg', 46.00, '小吃', '原料：中筋面粉、糖、猪油、水、面粉、猪油、红豆沙馅。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (613, '醪糟蛋', '/api/img/2021080416280618817381958079.jpg', 43.00, '小吃', '原料：鸡蛋、醪糟、冷冻汤圆、白砂糖。', '“四川美味。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (614, '烀苞米', '/api/img/2021080416280495643031958079.jpg', 28.00, '小吃', '原料：玉米、水。', '“一个烀字，方显东北特色。只是我这数量远远不够。一般是一堆生玉米扒一堆玉米皮和须子，要蒸一大锅，上桌是一大盆，才过瘾。可以直接啃，也可以和烀茄子、烀土豆及蒸酱一起吃，吃不完的还可以冻到冰箱里，随时吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (615, '清凉双色冰粉粉，红糖的甜蜜、抹茶的清新，解暑又美味香甜。', '/api/img/2021080316279826124038197577.jpg', 31.00, '小吃', '原料：清水、冰粉、红糖、抹茶粉、白砂糖、牛奶。', '“炎热的天，来试试这款清凉双色冰粉粉吧。入口微甜、清凉香甜、想吃啥就加啥。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (616, '免油炸芝士热狗棒', '/api/img/2021080316279573452048197577.jpg', 57.00, '小吃', '原料：培根火腿肠、面粉、鲜酵母、水、盐、糖、面包糠、马苏里拉奶酪。', '“剩下的一段肉肠，拿来做成芝士热狗棒吧不喜欢油炸，变炸为烤，油炸的看起来颜色更加金黄，表面油润；烤的颜色似乎就没那么诱人，表面干脆。口感略有差异，但都一样的芝香浓郁～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (617, '钵钵鸡', '/api/img/2021080316279549896478197577.jpg', 44.00, '小吃', '原料：鸡、牛肉、西兰花、海带、豆腐皮、鹌鹑蛋、姜、植物油、盐、八角、桂皮、小茴香、花椒、草果、香叶、生抽、老抽。', '“粗茶淡饭最养人，平平淡淡才是真。记录生活中的点点滴滴！家庭版钵钵鸡，家里有什么用什么，喜欢口味浓的可以加火锅底料或者直接买钵钵鸡调料😀” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (618, '麻酱面藕', '/api/img/2021080316279549292548197577.jpg', 22.00, '小吃', '原料：面藕、蒜蓉、香菜、小米椒、盐、生抽、陈醋、香油、芝麻酱。', '“夏日爽口小菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (619, '鲜贝丝瓜汤粉', '/api/img/2021080316279547686418197577.jpg', 16.00, '小吃', '原料：米粉、鲜贝丝瓜汤、清水、食盐、辣椒油。', '“做的鲜贝丝瓜汤，剩了一碗。煮点米粉做成汤粉，做一餐来食，三大营养元素就齐全啦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (620, '脆皮红薯球', '/api/img/2021080316279546609408197577.jpg', 49.00, '小吃', '原料：红薯、白砂糖、糯米粉、清水、锡纸。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (621, '饺子皮版苹果派', '/api/img/2021080316279546266178197577.jpg', 51.00, '小吃', '原料：饺子皮、苹果、黄油、白糖、淀粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (622, '烧仙草椰青酸奶水果捞', '/api/img/2021080216278947554868197577.jpg', 48.00, '小吃', '原料：烧仙草、椰青、火龙果、酸奶、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (623, '家常大冷面', '/api/img/2021080116277896869621958079.jpg', 11.00, '小吃', '原料：冷面、牛肉、鸡蛋、黄瓜、香菜、蒜沫、姜沫、西红柿、辣白菜、雪碧、酱油、米醋、盐、味精、鸡精、南德调味料、花椒油、白芝麻。', '“炎炎夏日，给家人做个大冷面，解暑又解馋，孩子们喜欢的不得了！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (624, '铜锣烧', '/api/img/2021080116277828985031958079.jpg', 33.00, '小吃', '原料：鸡蛋、糖粉、无铝泡打粉、低粉、豆沙馅、牛奶。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (625, '地瓜干', '/api/img/2021073116277223297528197577.jpg', 35.00, '小吃', '原料：红心地瓜、红茶、白糖。', '“地瓜干是小时候经常吃的美味，现在基本不用自己做了，福建的连城地瓜干就很出名。这次刚好买了一袋地瓜，吃不完就做成地瓜干制作的过程虽然辛苦，但是也是一种乐趣。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (626, '元宝姐姐的缤纷冰粉', '/api/img/2021073116277197501778197577.jpg', 14.00, '小吃', '原料：冰粉粉、水果糖、黄桃（选自己喜欢的）、酒酿、红枣、红糖、开水。', '“这次回重庆，又多了一样带不走的：也是重庆大街小巷都有的一道街边甜品：冰粉看着孩子从火车上一路哭回上海，为了缓解这一抹乡愁，老母亲决心在以后的夏日，都为她学做这一道很平常的重庆小甜点～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (627, '芝士黑麦馒头片', '/api/img/2021100616335029028538197577.jpg', 10.00, '西餐', '原料：黑麦馒头、马苏里拉奶酪、批萨酱、黄油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (628, '芝士厚蛋烧三明治', '/api/img/2021100616335006353778197577.jpg', 54.00, '西餐', '原料：厚吐司、安佳淡味黄油、安佳芝士片、鸡蛋、牛奶、培根、欧芹、蛋黄酱、盐。', '“我最喜欢吃三明治，每天我都会拿它来当早餐，今天早餐做一个这样的三明治。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (629, '牛排', '/api/img/2021100616334971192728197577.jpg', 39.00, '西餐', '原料：西冷牛排、小土豆、芦笋、小番茄、无花果、迷迭香、黑胡椒、盐、橄榄油、糖、肉桂粉、帕玛森干烙粉。。', '“家里最近穷了，妈妈和我，爸爸都想特别吃西餐。我觉得去吃西餐太贵了。没那么多钱，所以花了一些钱，买一些便宜的材料，做牛排给爸爸妈妈吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (630, '早餐必备，松软香甜超好吃的奶香排包！', '/api/img/2021100616334840285758197577.jpg', 33.00, '西餐', '原料：日式吐司面包粉、奶粉、鸡蛋、淡奶油、炼乳、牛奶、奶油奶酪、细砂糖、黄油、耐高糖酵母、盐。', '“添加了淡奶油跟奶油奶酪的奶香排包，柔软香甜，直接撕着吃，真真的老好吃了！这款奶香排包做法也不是很难，非常适合烘焙新手，准备好材料跟我做起来吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (631, '奶油滑鸡烩宽面', '/api/img/2021100216331554724518197577.jpg', 31.00, '西餐', '原料：意大利宽面、鸡腿肉、鸡腿菇、蒜、味淋、酱油、料酒、高汤、鲜奶油、黑椒盐、欧芹碎。', '“午餐时间～散发着蒜香&菌香&奶香的酱汁，配意大利宽面Fettuccine🍝。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (632, '波兰种面包', '/api/img/2021100216331387733918197577.jpg', 37.00, '西餐', '原料：高筋粉、波兰种、盐、糖、牛奶、奶粉、高筋粉、牛奶、耐高糖酵母、耐高糖酵母、黄油。', '“今天做了一个波兰种面包，需要的话看一下真的超香😘😘” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (633, '水果奶油可丽饼', '/api/img/2021093016330077764098197577.jpg', 20.00, '西餐', '原料：JUST Egg 植物蛋、清水、玉米油、细砂糖、低筋面粉、淀粉、奶油、青提葡萄。', '“现在越来越多的朋友和我一样，每月会选择几天茹素。除了传统的蛋奶素，也有许多朋友选择纯素。最近早上路过这家永康路口的煎饼小屋照例买早点，发现他们家还提供纯植物造的植物蛋煎饼，居然价格和普通煎饼一样。之前就听说过，这次不仅尝试了一下，而且今日份食谱也是用同款植物蛋做的哦，就是这份颜值在线的可丽饼🌯，男女老少皆食得！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (634, '猪排意面', '/api/img/2021092916328935924138197577.jpg', 20.00, '西餐', '原料：里脊肉、意面、酱油、盐、糖、胡椒粉、花椒粉、五香粉、黑胡椒粉、油、番茄酱、蚝油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (635, '椰香吐司（一次发酵）', '/api/img/2021092916328860311158197577.jpg', 54.00, '西餐', '原料：高筋面粉、鸡蛋2个+椰汁、白糖、酵母、盐、玉米油、玉米油、椰蓉、白糖、鸡蛋、鸡蛋液（或蜂蜜水、牛奶）。', '“本款吐司用椰汁揉面，卷入椰蓉馅，椰香浓郁，蓬松柔软，皮薄拉丝，营养美味。采用冷藏泡面（静置），面团经过较长时间的静置，自然形成面筋，纯手工揉面也能很快揉出手套膜，极大缩短揉面时间，同时，采用一次发酵，省时省力，值得一试。本配方为二个450克吐司的用量。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (636, '牛肉番茄辣酱意面', '/api/img/2021092816328089779031958079.jpg', 27.00, '西餐', '原料：意面、牛肉、番茄、洋葱、大蒜、油、辣酱、蕃茄酱、料酒、酱油、胡椒粉、水、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (637, '香煎牛排骨配蘑菇汁', '/api/img/2021092616326343619108197577.jpg', 26.00, '西餐', '原料：牛排骨、蘑菇、西兰花、蘑菇、奶油、味淋、黑椒盐、高汤、蒜、黑椒盐、欧芹碎。', '“自制蘑菇汁是重点，拌饭一流😋。牛排骨可用任意牛排代替，家有宝宝要煎到全熟，买肥瘦相间的，带大理石纹的牛肉比较嫩滑。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (638, '杂锦西多士', '/api/img/2021092616326343069288197577.jpg', 38.00, '西餐', '原料：面包、牛油、芝士、草莓果酱、花生酱、蛋液、炼乳。', '“下午茶时间☕️  French Toast～牛油芝士，草莓果酱，花生酱。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (639, '海鲜蘑菇披萨🍕', '/api/img/2021092416324834822428197577.jpg', 24.00, '西餐', '原料：高筋面粉、低筋面粉、酵母粉、糖、盐、油、牛奶、虾（蒜蓉腌过的虾）、鱿鱼圈、蘑菇、蘑菇、奶油、高汤、蒜瓣、黑椒盐。', '“制作全过程适合4岁以上宝宝操作😊。用自制的奶油蘑菇汁比番茄酱好吃太多了😋。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (640, '原味小餐包', '/api/img/2021092416324697742238197577.jpg', 23.00, '西餐', '原料：牛奶、低粉、酵母粉、白糖、鸡蛋、面包粉、黄油、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (641, '煎牛排', '/api/img/2021092316323674195278197577.jpg', 30.00, '西餐', '原料：黄油、牛排、黑椒汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (642, '白汁培根披萨🍕', '/api/img/2021092216323157536528197577.jpg', 12.00, '西餐', '原料：高筋面粉、低筋面粉、糖、酵母、盐、油、牛奶、蒜蓉虾（提前用蒜蓉腌制的）、培根、洋葱、奶油、培根、洋葱、黑椒、味淋、料酒。', '“自制奶油洋葱汁，配培根蒜蓉虾。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (643, '奥尔良炸鸡翅', '/api/img/2021092216323134417798197577.jpg', 54.00, '西餐', '原料：鸡翅、面粉、生粉、奥尔良烤鸡腌料。', '“奥尔良鸡翅炸着吃😋。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (644, '香煎小面包，制作简单，奶香味十足，一口一个，孩子超爱吃。', '/api/img/2021092016321231956248197577.jpg', 19.00, '西餐', '原料：鸡蛋、植物油、白砂糖、酵母、温牛奶、普通面粉、盐。', '“小小一个煎面包，制作简单，奶香味十足，一口一个，幸福感满满。吃的时候还可以撒上点糖粉，或者蜂蜜等等，更添风味哦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (645, '健康低脂代餐～日式土豆鸡蛋沙拉', '/api/img/2021091716318423993408197577.jpg', 51.00, '西餐', '原料：土豆、鸡蛋、火腿片、沙拉酱、盐、黑胡椒粉、牛奶。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (646, '椰香餐包卷（一次发酵）', '/api/img/2021091416316027425428197577.jpg', 42.00, '西餐', '原料：高筋面粉、椰汁、酵母、玉米油、椰子粉、白糖、鸡蛋、盐。', '“用浓郁香醇的椰汁、椰粉揉面，营养美味，香甜可口，且，采用冷藏泡面、一次发酵的方法，省时省力，值得一试。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (647, '龙利鱼汉堡', '/api/img/2021090316306336178178197577.jpg', 50.00, '西餐', '原料：面包、龙利鱼、火腿肠、鸡蛋、生菜叶、玉米粒、胡萝卜、葱、姜、蒜、料酒、生抽、蚝油、冠利酸甜青瓜蓉汉堡酱。', '“面包里加它，低脂饱腹，能量满满。开学季，早餐不能忽视，鸡蛋牛奶不能少，多吃蔬菜水果，推荐今日份早餐，面包与生菜、玉米粒、胡萝卜、龙利鱼、搭配，好吃又简单，重要的是低脂低卡，吃着瘦的减脂餐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (648, '茄汁火山土豆泥', '/api/img/2021090216305821135128197577.jpg', 33.00, '西餐', '原料：土豆、胡萝卜、西红柿、冷冻蔬菜、橄榄油、六月鲜10克轻盐特级原汁酱油。', '“有一次在饭店里吃到这款土豆泥，感觉很有创意，也非常好吃，回来就做了一些小小的改良，添加了胡萝卜泥，替换成了茄汁口味，这样可以更适合小朋友和不喜欢吃辣和重口味的小伙伴，将土豆泥浸在茄汁里，让我回想起了肯德基里经典的那款鸡汁土豆泥，一样好吃的停不下来。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (649, '黑全麦广式腊肠面包卷', '/api/img/2021090116304629042748197577.jpg', 32.00, '西餐', '原料：高筋粉、黑全麦粉、鲜酵母、白砂糖、盐、牛奶、水、鸡蛋、黄油、广式香肠（蒸熟切碎）。', '“广式腊肠蒸熟切碎，卷进黑全麦的面团里藏不住的小肉丁在卷卷两端探头探脑看起来就肉香满满。。。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (650, '面包控不可错过的一款小面包，香味十足巨好吃！', '/api/img/2021090116304601154928197577.jpg', 28.00, '西餐', '原料：日式吐司面包粉、低筋面粉、奶粉、牛奶、淡奶油、炼乳、细砂糖、黄油、耐高糖酵母、有机黑芝麻酱、白芝麻。', '“香味超浓郁的黑芝麻小面包，柔软香甜，造型简单，学会了早餐再也不用愁了！两股辫子扭一下，塞进模具，造型别提多简单了，手残党都不用怕，操作零难度，颜值也的说的过去的！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (651, '土豆泥 冷热双拼', '/api/img/2021083016303182034078197577.jpg', 60.00, '西餐', '原料：土豆、蟹柳棒、蟹籽、乌冬汁。', '“煮了好大一碗土豆泥，于是做了一热一凉两种吃法。如果喜欢肯德基味道的土豆泥，把我调的热汁换成Gravy Sauce就行，网上应该不难买到。这样就变成土豆泥三吃啦😊。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (652, '白汁蛤蜊煮意面', '/api/img/2021083016303151484168197577.jpg', 52.00, '西餐', '原料：意面、新鲜蛤蜊、培根、口蘑、蒜头。', '““白汁xx”菜品很多时候是给人粘糊糊油腻腻的感觉，食材像泡在面粉糊里似的，稍微年长人士尤其不喜欢。今天做的这款白汁蛤蜊意面，汤汁清爽不油腻，意面根根分明不会沾粘，装盘后只在盘子底部留有薄薄一层白汁。油量极少，盐分极少，水量也极少，汤汁来源是半碗水，淡奶油，还有蘑菇和蛤蜊熟透时出的水，被意面充分吸收，非常鲜甜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (653, '培根煎土豆泥', '/api/img/2021083016303014867278197577.jpg', 15.00, '西餐', '原料：培根、土豆（小的）、熟蛋黄、食盐、黑胡椒粉。', '“感觉一直在吃，冰箱和冰柜还一直是满的，各位亲，你们是不是也是这样？！翻出来一袋培根，做个土豆泥来吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (654, '华夫饼', '/api/img/2021082916302259585538197577.jpg', 52.00, '西餐', '原料：蛋糕粉、鸡蛋、白砂糖、酵母粉、醋。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (655, '广式鸭润腊肠面包卷', '/api/img/2021082916302060157588197577.jpg', 16.00, '西餐', '原料：高筋粉、蛋黄、白砂糖、盐、鲜酵母、鲜牛奶、黄油、广式鸭润腊肠（蒸熟）。', '“一包广式鸭润腊肠，看起来黑乎乎不太讨喜卷进面团里，尝尝不一样的腊肠面包卷黄灿灿的蛋黄把面团染成金色，掩盖了黑乎乎的肠最终发酵完成的面包卷齐刷刷地向左旋转了小30度，成斜向排放，真有意思” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (656, '巨无霸汉堡', '/api/img/2021082716300715682718197577.jpg', 22.00, '西餐', '原料：汉堡面包、切片芝士、烤牛肉排、煎蛋、生菜、牛肉、黄油、鸡蛋、蒜、洋葱、面粉、料酒、黑胡椒粉、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (657, '双味脆嫩炸虾排', '/api/img/2021082316297103273948197577.jpg', 55.00, '西餐', '原料：大虾、鸡蛋、金黄面包糠、番茄沙司、蜂蜜芥末沙拉酱。', '“想吃酥香的美味但又不想摄入太多的油脂，空气炸锅可就派上用场了，瞧咱这金灿灿的无油炸虾排，咬一口扑嗦嗦，好吃解馋无负担。虾直接白水煮最省事，但老是一种吃法难免厌烦了。多花一点儿时间做成虾排，无油烤一烤甭提多好吃了。10只虾鼓捣鼓捣，蘸着番茄沙司，或者蘸着沙拉酱，别具风味。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (658, '奶油意面', '/api/img/2021082216296123319691958079.jpg', 27.00, '西餐', '原料：意面、小番茄、香菇、胡萝卜、西兰花、培根、洋葱、橄榄油、芝士粉、黑胡椒粉、盐、奶炖小方', '“赶紧试试这一款奶油意面，芝士的香味和黑胡椒辣味完美的融合在一起，奶香浓郁但不腻口，做法也特别简单呦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (659, '满口都充满幸福感的拉丝～芝士焗吐司', '/api/img/2021082116295495343358197577.jpg', 54.00, '西餐', '原料：吐司、芝士碎、蔬菜粒、虾仁、鸡蛋、洋葱、牛奶、料酒、胡椒粉。', '“健康营养，元气满满的芝士焗吐司吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (660, '萨拉米甜辣面包卷', '/api/img/2021082116295164123068197577.jpg', 54.00, '西餐', '原料：高筋粉、细砂糖、盐、全蛋、牛奶、鲜酵母、黄油、拌饭酱、萨拉米香肠。', '“看中一款萨拉米青酱面包卷，却没有青酱。手中一瓶拌饭酱，有点像甜辣酱的口味，索性用来代替青酱。酱料抹在面片上才知道原来这酱里还有些个花椒大小的颗粒，有点像肉肉。烘烤后这酱料似乎隐去了些许甜味，而辣味却变得明显” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (661, '香蕉圣女果鸡蛋沙拉', '/api/img/2021082016294547489078197577.jpg', 38.00, '西餐', '原料：黄樱桃圣女果、香蕉、鸡蛋、苦菊、沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (662, '玉米火腿鸡蛋沙拉', '/api/img/2021082016294460123618197577.jpg', 30.00, '西餐', '原料：苦菊、玉米、火腿肠、鸡蛋、味好美沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (663, '麻薯欧包', '/api/img/2021082016294374829628197577.jpg', 21.00, '西餐', '原料：高筋粉、白砂糖、盐、可可粉、酵母、鸡蛋、黄油、糯米粉、玉米淀粉、白砂糖、黄油、牛奶、水果丁、红枣丁、葡萄干', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (664, '鸡蛋煎面包片', '/api/img/2021082016294233701088197577.jpg', 10.00, '西餐', '原料：面包片、鸡蛋、紫薯泥、白糖、黄油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (665, '土豆泥沙拉', '/api/img/2021081916293639383218197577.jpg', 35.00, '西餐', '原料：土豆、黄瓜、胡萝卜、洋葱、鸡蛋、玉米粒、火腿/午餐肉、牛奶、蛋黄酱、沙拉酱、黑胡椒。', '“营养又美味，健康又减脂 😋” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (666, '黄瓜烤肠沙拉', '/api/img/2021081816292647447888197577.jpg', 33.00, '西餐', '原料：烤肠、黄瓜、煎蛋、无花果、海盐黑胡椒。', '“夏季懒人餐，非沙拉莫属。不是盈利的西餐，不需要特定食材，家里有啥就用啥材料。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (667, '西兰花烤肠沙拉', '/api/img/2021081816292646659718197577.jpg', 16.00, '西餐', '原料：西兰花、烤肠、酸奶麦片、葡萄、海盐黑胡椒。', '“家里没有别的配菜，也没备主食，所以放了酸奶麦片来搭配。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (668, '卤菜意大利面', '/api/img/2021081816292508508011958079.jpg', 44.00, '西餐', '原料：意大利面、卤鸡中翅、卤蛋、卤鸭心、黑椒汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (669, '黑胡椒牛排意面', '/api/img/2021081816292509641101958079.jpg', 41.00, '西餐', '原料：意面、牛排、小番茄、西蓝花、意面黑胡椒酱。', '“仪式感很重要，偶尔的小惊喜小浪漫，会对生活或者感情大大加分哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (670, '免揉迷你小面包，省时又省力，10分钟揉出膜，面包拉丝，入口柔软。', '/api/img/2021081716292024052928197577.jpg', 20.00, '西餐', '原料：高筋面粉、白砂糖、盐、温牛奶、酵母、鸡蛋、无盐黄油。', '“今天用这个水合法的方法来做面包，是小高姐那边学的，非常的不错，试了几次省时省力，就算是新手做也基本不会失败，制作简单省时省力，10分钟揉出膜，出来的面包也是拉丝的，入口非常柔软。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (671, '迷你火腿肠面包', '/api/img/2021081616290968783268197577.jpg', 20.00, '西餐', '原料：牛奶、面包粉、低粉、白糖、鸡蛋、黑芝麻、黄油、酵母粉、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (672, '迷你小面包', '/api/img/2021081616290842269968197577.jpg', 47.00, '西餐', '原料：面包粉、低粉、牛奶、鸡蛋、白糖、白芝麻、酵母粉、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (673, '黑椒鳕鱼', '/api/img/2021081516290308386058197577.jpg', 25.00, '西餐', '原料：鳕鱼、胡萝卜、蘑菇、芦笋、玉米淀粉、海盐、黑胡椒碎。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (674, '热狗包', '/api/img/2021081516289913639708197577.jpg', 31.00, '西餐', '原料：中筋面粉、清水、白糖、食盐、酵母、黄油、热狗。', '“平时做的热狗包都是用现坯包着热狗烘的，这次和面后想搞搞新意思，把热狗放在上面划花，烤起来搜狗的肉香就能充分地挥发出来，表面花纹绽放得清清楚楚，甚是吸引。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (675, '黑椒牛排', '/api/img/2021081516289912298328197577.jpg', 20.00, '西餐', '原料：牛肉、黑胡椒粉、盐、橄榄油、葱姜水、橄榄油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (676, '白菜炒意大利面', '/api/img/2021081316288354382328197577.jpg', 26.00, '西餐', '原料：白菜、意大利面、西红柿、玉米油、盐、生抽。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (677, '鸡蛋肉松三明治', '/api/img/2021081216287322055518197577.jpg', 10.00, '西餐', '原料：面包片、鸡蛋、海苔肉松、植物油、沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (678, '牛排黄瓜能量沙拉', '/api/img/2021071716265050263491958079.jpg', 54.00, '西餐', '原料：牛排、鸡胸肉、培根、黄瓜、青豆粒、玉米粒、口蘑、色拉油、盐、黑胡椒粉、混合香辛料、沙拉酱。', '“能量满满，却又低脂的沙拉，健身人士的最爱～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (679, '黑椒牛排意面', '/api/img/2021071516263602523571958079.jpg', 49.00, '西餐', '原料：菲力牛排、意面、鸡蛋、西红柿、蒜瓣、黑椒酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (680, '藜麦乌鸡蛋沙拉', '/api/img/2021070616255495164548197577.jpg', 15.00, '西餐', '原料：乌鸡蛋、藜麦、白玉黄瓜、西兰花、红心火龙果、食盐。', '“本来还在纠结吃啥沙拉呢，主页就显示藜麦菜谱了，就地做个藜麦沙拉。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (681, '萌萌的卡通吐司，不用担心小朋友不爱吃早餐喽', '/api/img/2021070616255351563328197577.jpg', 56.00, '西餐', '原料：凯萨琳吐司粉、奶粉、蛋液、淡奶油、炼乳、牛奶、细砂糖、黄油。', '“哄娃必备的卡通小吐司，可可爱爱的造型，操作起来也很简单，简单无难度！切片后简单装饰一下，绝对让小朋友眼前一亮！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (682, '酥脆薯塔', '/api/img/2021070516254780521358197577.jpg', 45.00, '西餐', '原料：土豆、玉米淀粉、番茄酱、椒盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (683, '超柔软的肉松火腿面包', '/api/img/2021070516254489152288197577.jpg', 12.00, '西餐', '原料：高筋粉、鸡蛋液、牛奶、酵母、糖、黄油、盐、火腿、肉松、鸡蛋液、沙拉酱、香葱。', '“崽崽不爱吃甜面包，偏偏对咸面包爱不释口，唉！为难老妈子我了，每天变着花样给他做各种各样的咸面包。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (684, '萨拉米新奥尔良鸡肉双拼披萨', '/api/img/2021070416253637848768197577.jpg', 57.00, '西餐', '原料：面粉、鲜酵母、细砂糖、盐、黄油、橄榄油、水、马苏里拉奶酪、自制披萨酱、玉米粒、青椒、萨拉米香肠、鸡腿、新奥尔良烤肉料、水。', '“鸡肉披萨意犹未尽，想起还有几片萨拉米不如来个萨拉米与鸡肉双拼，萨拉米与鸡腿肉成对角扇形摆放切下的每个四分之一块都会是萨拉米与鸡肉双拼同一块披萨里有不同的味道。。。。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (685, '新手也能成功的黑胡椒烤牛排', '/api/img/2021070116251313411798197577.jpg', 53.00, '西餐', '原料：肉眼牛排、大喜大黑胡椒烤肉酱、橄榄油。', '“每次去西餐厅必点烤牛排，好吃的牛排一个要控制煎制时间，一个要有灵魂酱汁，今天做个速成版烤牛排，酱汁是现成的，对于新手朋友们特别友好！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (686, '咸蛋香肠意大利螺丝粉', '/api/img/2021070116251094017278197577.jpg', 38.00, '西餐', '原料：意大利螺丝粉、咸蛋、香肠、油、盐、芝士。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (687, '新奥尔良鸡肉披萨', '/api/img/2021070116251081594168197577.jpg', 60.00, '西餐', '原料：面粉、鲜酵母、细砂糖、盐、黄油、橄榄油、水、马苏里拉芝士、自制披萨酱、玉米粒、青椒、鸡腿、新奥尔良烤肉料、水。', '“不想直接吃各种新奥尔良鸡。。。。就来做个新奥尔良鸡肉披萨，大多数的做法会事先把鸡肉炒到熟或半熟，未免太费工时。索性把生鸡肉直接放进披萨烤熟，鸡肉不要太大块，应该会烤透。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (688, '元气三明治', '/api/img/2021063016250309148758197577.jpg', 33.00, '西餐', '原料：吐司、鸡蛋、生菜叶、西红柿、洋葱、沙拉酱、番茄酱。', '“天热不爱下厨房，三明治是最好的快手早餐，三五分钟端上桌，无论是搭配一杯牛奶，还是搭配一碗粥，都是简单又富有营养的美味，重要的是大人孩子都爱吃，而且做法非常简单，比买得实惠，还能让孩子多吃蔬菜，补充维生素，简单又美味，孩子吃得香。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (689, '黑椒牛柳意面', '/api/img/2021063016250167748661958079.jpg', 23.00, '西餐', '原料：意面、黑椒牛柳、海盐、黑胡椒、奶酪碎、罗勒碎。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (690, '鸡胸蔬菜沙拉', '/api/img/2021063016250169152138197577.jpg', 31.00, '西餐', '原料：鸡胸肉、西兰花、胡萝卜、包菜、黑芝麻、油醋汁、姜、盐、葱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (691, '   手抓饼牛肉肠热狗', '/api/img/2021062916249548955728197577.jpg', 25.00, '西餐', '原料：手抓饼、牛肉肠。', '“最近高温，天热的很，厨房不能多呆。用手抓饼给孩子做一个热狗，快速简单又好吃。今天分享一个用手抓饼做的热狗，用的自己蒸的牛肉肠，我用的手抓饼也是自己做的，自己做的不会用起酥油，而且自己做的油酥，吃起来更健康。我用的三明治多用锅，没有的话用平底锅也可以的。手抓饼不想自己做，超市有卖现成的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (692, '意大利面', '/api/img/2021062616246706843228197577.jpg', 35.00, '西餐', '原料：意大利粉、番茄、洋葱、香肠、牛油、盐、番茄酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (693, '芝士爆浆土豆球', '/api/img/2021062516246006192008197577.jpg', 44.00, '西餐', '原料：土豆、芝士碎、鸡蛋、玉米淀粉、面包糠、盐、黑胡椒粉。', '“家里的小朋友是个吃肉的小怪兽，只要有肉就不吃菜类型的，不管什么菜，要想让他吃都要费点周折，其中也包括土豆，今天分享的芝士爆浆土豆球就是用土豆做的零食，中间包了芝士做馅料，芝士是高钙很高的奶制品，就像孩子平时爱吃的奶酪棒，很适合用来做辅食，用烤箱也能做到外皮酥脆，不油炸，更健康，蘸点番茄酱，孩子特别喜欢吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (694, '奶油三文鱼汤', '/api/img/2021062516245853689458197577.jpg', 20.00, '西餐', '原料：三文鱼、土豆、口蘑、小洋葱、黄油、淡奶油（脂含量15%）、芦笋、盐、现磨胡椒、料酒、百里香。', '“非常爱吃鱼类和海鲜，这次来做一道非常简单几乎零失败的奶油三文鱼汤。食材尽量买有机的，虽然贵一点，但是味道确实更好吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (695, '松饼', '/api/img/2021062416245412933878197577.jpg', 35.00, '西餐', '原料：低筋面粉、酸奶、鸡蛋、糖、黄油。', '“用平底锅来做蛋糕，真的是太简单了，太方便了，好吃还不上火，早晨时间紧迫也可以做，只要十分钟就能上桌，再配上一杯牛奶或是咖啡，尽情品味美味吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (696, '鸡胸肉拌时蔬沙拉', '/api/img/2021062416245396922668197577.jpg', 35.00, '西餐', '原料：即食鸡胸肉、黄瓜、煮鸡蛋、蓝莓、苦苣、油醋汁。', '“夏日美味～最爱时蔬沙拉，简单方便，营养不流失，非常适合炎热的夏天～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (697, '土豆沙拉', '/api/img/2021062416245128805058197577.jpg', 11.00, '西餐', '原料：土豆、胡萝卜、黄瓜、圆葱、鸡蛋、火腿肠、沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (698, '精肉土司沙拉', '/api/img/2021062316244204266508197577.jpg', 46.00, '西餐', '原料：精肉、吐司、紫甘蓝、黄瓜、洋葱、甜杏、煎焙芝麻酱。', '“吃了一段时间的瘦身沙拉，把少爷他爹给我喂胖的肉肉减没啦！继续沙拉，这次用了沙拉酱哦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (699, '藜麦贝果', '/api/img/2021061816240030531948197577.jpg', 49.00, '西餐', '原料：金龙鱼面包用小麦粉、熟藜麦、酵母、糖、橄榄油、水、盐。', '“今天我向大家推荐一个快手早餐，做起来简单又方便。它既可以象汉堡那样切开夹菜夹蛋，也可以象我这样创新一下，将蛋放在上面，金色的蛋黄，如同一轮满月，好看又好吃。比馒头包子更有新意，吃起来又香又软，营养还全面。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (700, '低脂沙拉', '/api/img/2021061816240023654388197577.jpg', 46.00, '西餐', '原料：小番茄、西兰花、生菜、甜虾、玉米粒、味极鲜、醋、蜂蜜、香油、白芝麻、蒜末。', '“减肥应该算是女人们终身奋斗的事业，很多人靠饿来减肥，我之前也是，但是饥饿感真的是非常难受，而且会让人情绪低落，不快乐，所以后来也不太能挨饿了，可能随着年龄的增长，人就会越来越不抗饿了！没办法，想保持身材，还要吃饱，那就必须调整饮食结构，要吃热量含量低的食物来增加饱腹感，往往这样的食物都很难吃，所以渐渐的总结出味道还不赖的沙拉汁，我称之为万能沙拉汁，哈哈，用家常的调料和普通低脂的食材制作的沙拉，吃起来味道很不错呢，所以今天特地拿出来分享。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (701, '黄瓜鸡蛋沙拉', '/api/img/2021061816239942983908197577.jpg', 27.00, '西餐', '原料：黄瓜、鸡蛋、无、食盐。', '“婆婆自己种的黄瓜，老太太从来不舍得趁黄瓜还嫩的时候摘，感觉都是成老黄瓜啦，才舍得摘。老公带了有十斤的黄瓜，把好一点的分给亲友啦，剩下的歪瓜裂枣就是我的。不管咋样，是纯绿色食物又是减肥佳品不是。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (702, '松软带拉丝的一发毛毛虫肉松卷，快手咸味面包！早餐轻松安排！', '/api/img/2021061616238071992108197577.jpg', 59.00, '西餐', '原料：高筋面粉、牛奶、全蛋液、奶粉、细砂糖、盐、耐高糖干酵母、黄油、刷表面全蛋液、海苔肉松、沙拉酱、船型油淋膜。', '“吃腻了甜面包，试试这一款比较受欢迎的咸味肉松面包！裹了海苔肉松和沙拉酱，一口咬下，咸甜适中，口感非常赞！两种整形方法，一次发酵，比较快手！松软带拉丝的哦，第一种整形方法略复杂一些，第二款简单一些，两款颜值都还可以！一次制作11只，自留一些，投喂一些，刚刚好！配方粉量较大，不适合手揉。建议厨师机揉面。我用的是PE6080，85L大容量烤箱，如果烤箱不够大，建议用2/3材料操作。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (703, '黑椒鸡胸', '/api/img/2021061516237351555718197577.jpg', 35.00, '西餐', '原料：鸡胸、黑胡椒、盐、黑椒汁。', '“简单低脂的肉类。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (704, '蜂蜜小面包', '/api/img/2021061416236344152118197577.jpg', 11.00, '西餐', '原料：高筋粉、鸡蛋、牛奶、酵母、盐、黄油、黄油、蜂蜜、白砂糖、橄榄油', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (705, '香菇肉酱拌意面', '/api/img/2021061216234651933458197577.jpg', 16.00, '西餐', '原料：意面、肉末、香菇、葱花、油、酱油、番茄酱。', '“好美味的意面，精髓在于酱，香菇肉酱咸香味美。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (706, '培根蘑菇浓汤', '/api/img/2021061116233802331128197577.jpg', 49.00, '西餐', '原料：培根、蘑菇、洋葱、玉米油、盐。', '“简单的蘑菇浓汤简单的操作动手起来” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (707, '培根南瓜浓汤', '/api/img/2021061116233792694301958079.jpg', 60.00, '西餐', '原料：培根、南瓜、洋葱、玉米油、盐。', '“一道简单好喝的美味浓汤！！！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (708, '奶酪鸡蛋饼', '/api/img/2021061116233753475638197577.jpg', 59.00, '西餐', '原料：鸡蛋、火腿肠、马苏里拉奶酪、洋葱、植物油、番茄酱、沙拉酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (709, '秒杀肯德基，吮指香酥麦乐鸡块！', '/api/img/2021060916232216088498197577.jpg', 27.00, '西餐', '原料：鸡胸肉、土豆、葱、鸡蛋、玉米淀粉、玉米油、生抽、黑胡椒粉、番茄酱。', '“今天用鸡胸肉，给小朋友们安排一款烤箱版的麦乐鸡块，相对油炸更为少油少脂，孩子吃也放心！蘸点番茄酱，吃起来更加解腻，酸甜香酥也太好吃了吧。快手易做，赶紧给家中的萌娃安排吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (710, '红糖面包', '/api/img/2021060916232103912968197577.jpg', 42.00, '西餐', '原料：面包粉、低粉、红糖、鸡蛋、清水、黄油、白芝麻、酵母。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (711, '抹茶条纹面包', '/api/img/2021060916232103216728197577.jpg', 25.00, '西餐', '原料：面包粉、低粉、白糖、酵母粉、鸡蛋、牛奶、黄油、卡仕达（装饰）酱黄油、抹茶粉、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (712, '萨拉米披萨', '/api/img/2021060916232074217978197577.jpg', 54.00, '西餐', '原料：面粉、鲜酵母、细砂糖、盐、黄油、橄榄油、水、马苏里拉芝士、自制披萨 酱、玉米粒、萨拉米香肠。', '“关于萨拉米的很多吃法，看来看去还是更愿意做萨拉米披萨。不想搞太多料，就这么简简单单一层萨拉米就好。奶酪与肉肠，配上披萨酱中的香草味简单披萨，也似不错。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (713, '鸡肉蔬菜沙拉', '/api/img/2021060816231500941918197577.jpg', 24.00, '西餐', '原料：鸡胸肉、白煮蛋、紫甘蓝、黄瓜、玉米粒、苦菊、沙拉汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (714, '焦糖吐司', '/api/img/2021060716230521489638197577.jpg', 33.00, '西餐', '原料：吐司面包、炼乳、鸡蛋、马苏里拉奶烙、白砂糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (715, '果蔬沙拉', '/api/img/2021060616229660459998197577.jpg', 11.00, '西餐', '原料：穿心莲、芒果、火龙果、洋葱、小水萝卜、冠利塔塔酱、冠利辣椒酱、冠利芥末青瓜蓉汉堡酱。', '“考生没胃口试试这道菜，清热解暑，爽口开胃，一碗饭不够吃。给大家推荐一道我家小朋友最爱的爽口下饭菜，尤其适合考生，清热解暑，爽口开胃，每次都要多做点饭，少了不够吃，超级下饭菜。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (716, '烤薯条', '/api/img/2021060616229630644158197577.jpg', 52.00, '西餐', '原料：大土豆、色拉油、辣椒粉、孜然粉、黑胡椒粉、盐、冠利凯撒沙拉酱。', '“薯条不用买，在家自己做，外边买一份8~11元，自己做2元孩子能吃饱，放心吃不长肉 ，好吃又健康。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (717, '三明治', '/api/img/2021060616229584148878197577.jpg', 16.00, '西餐', '原料：芝士、午餐肉、面包、黄油。', '“她吃了三明治是经和自己的手，做出来的才叫好吃哦！！！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (718, '香橙奶烙烤吐司', '/api/img/2021060616229500216788197577.jpg', 35.00, '西餐', '原料：吐司面包、牛奶、鸡蛋、马苏里拉芝士、白砂糖、橙子、柠檬。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (719, '青麦仁沙拉', '/api/img/2021060616229439345598197577.jpg', 21.00, '西餐', '原料：青麦仁、熟鹅蛋、水萝卜、火腿肠、油醋汁。', '“青麦仁是用已经长饱满但未熟的小麦粒速冻而成色泽碧绿，口味独特，味道清新爽口。青麦仁含有丰富的蛋白质、叶绿素、膳食纤维和α、β两种淀粉酶具有帮助人体消化、降低血糖的功能，是一种高营养的纯绿色食品。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (720, '煎奥尔良猪排', '/api/img/2021060616229435102088197577.jpg', 25.00, '西餐', '原料：猪排、奥尔良腌料粉、生姜、大葱、酱油、盐、胡椒粉、蚝油、淀粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (721, '松茸菇牛肉意面', '/api/img/2021060316227271304131958079.jpg', 25.00, '西餐', '原料：意面、牛肉、洋葱、松茸菇、盐、油、黑胡椒酱、料酒、酱油、蒜、水、五香粉、姜粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (722, '一口停不下来，好吃到舔手指的清新柠香黑胡椒烤鸡翅', '/api/img/2021053116224669616768197577.jpg', 25.00, '西餐', '原料：鸡翅、柠檬、米醋、蜂蜜、生抽、蚝油、料酒、葱段、姜片、迷迭香、黑胡椒、白芝麻、蜂蜜水。', '“清新不腻的黑胡椒烤鸡翅，加了柠檬片一起烤，鸡翅带着柠檬的清香，入口带点微酸。十分解腻，连吃3只都不会腻的哦！自己配调料，量没有特别精确到克数，家里的搪瓷勺子那种计量的一勺。三步走，备料-腌制-烤制，0难度！亲测小朋友爱吃的款！偶尔给小朋友加个餐，吃的超级香！另外：如果实在没有迷迭香可以省略！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (723, '肉松芝心三明治', '/api/img/2021053116224612368378197577.jpg', 28.00, '西餐', '原料：生菜、黄瓜、火龙果、肉松、咸蛋黄、沙拉酱、芝士片、吐司。', '“越吃越瘦的减脂三明治，让你每天都能享受身体轻盈，畅通无阻的快乐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (724, '奥尔良鸡翅', '/api/img/2021053116224609399028197577.jpg', 12.00, '西餐', '原料：鸡翅中、奥尔良鸡翅调料。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (725, '萌熊烤榴莲', '/api/img/2021053116224315761118197577.jpg', 51.00, '西餐', '原料：猫山王榴莲、吐司片、橄榄油。', '“榴莲烤着吃，臭味就会减少很多，而且更香甜软糯，不要说我这个榴莲控，就是平时不爱吃榴莲的也会爱上。我今天是用土司搭配榴莲来烤的，烤的酥脆的土司配香甜软糯如奶油般的榴莲，让人吃了就停不下来。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (726, '低脂全麦鸡肉卷饼', '/api/img/2021052516219207876878197577.jpg', 11.00, '西餐', '原料：王后全麦面粉、盐、开水、生菜、低脂辣椒酱或者沙拉酱、鸡胸肉。', '“减脂好帮手。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (727, '鸡胸肉蘑菇浓汤', '/api/img/2021052416218548573881958079.jpg', 45.00, '西餐', '原料：蘑菇、虾肉、面粉、牛奶、黑胡椒粉、料酒、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (728, '海盐奶油焦糖酱，用途百搭，做法巨简单，风味独特美妙。', '/api/img/2021052316217451872458197577.jpg', 42.00, '西餐', '原料：白砂糖、清水、淡奶油、无盐黄油、海盐。', '“美味百搭、简单快手的海盐焦糖酱，用途多多，与卡仕达酱混合，做马卡龙的馅，抹面包上味道甜蜜，做甜点上的淋面，做饼干、曲奇、馒头的蘸酱，等等各种应用。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (729, '淡奶油吐司❗️超级柔软拉丝，消耗淡奶油利器', '/api/img/2021052316217354735758197577.jpg', 46.00, '西餐', '原料：面包粉（新良）、鸡蛋、牛奶、耐高糖酵母、面包粉、糖、盐、淡奶油、黄油。', '“这是一款含有大量淡奶油的吐司，采用中种冷藏发酵法，做出来的吐司特别的柔软，吐司一层层的，拉丝效果很棒，奶香味十足，但是不甜腻，越吃越想吃那种，如果家里有用不完的淡奶油，可以尝试下这款吐司，做法简单，成功率高，味道没得说。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (730, '红糖面包', '/api/img/2021052116216034667588197577.jpg', 41.00, '西餐', '原料：高盘面粉、红糖、清水、鸡蛋、酵母、玉米油、盐。', '“红糖不仅可以冲水喝，还可以制作许多的美食，像红糖馒头，红糖面包，红糖糍粑……今天我要用红糖来制作面包，正好可以用来当第二天的早餐，小小的一个还可以追剧的时候当零食，晚睡的时候当宵夜，就是想怎么吃就怎么吃。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (731, '意大利拖鞋面包（恰巴塔）', '/api/img/2021052116216015573198197577.jpg', 42.00, '西餐', '原料：高筋面粉、低筋面粉、酵母、海盐、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (732, '牛奶吐司', '/api/img/2021052116216011201338197577.jpg', 54.00, '西餐', '原料：高筋面粉、鸡蛋、食盐、白糖、酵母、牛奶、黄油。', '“牛奶吐司是最基本的吐司，用到的食材种类最少，很适合刚刚接触烘焙的小伙伴，当然也适合不用囤太多食材的小伙伴，像我一样，家里只要有高筋面粉，想吃就做，最简单了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (733, '汉堡包', '/api/img/2021052116216009317328197577.jpg', 28.00, '西餐', '原料：高筋面粉、鸡蛋、牛奶、盐、糖、酵母、黄油、芝士、西红柿、生菜、沙拉酱、火腿。', '“这次的汉堡胚做得比较小，算是迷你小汉堡了，孩子一次也能吃两个呢。这样的小汉堡装进便当盒里也很方便，工作餐这样吃也很好呢。汉堡包的配菜可以复杂，也可以简单，这次放了火腿和蔬菜，再加上芝士片，一口咬下去咸甜混合在一起，还真不错，很好吃，火腿要是再煎一下，趁热吃就更香了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (734, '精肉芦笋土豆沙拉', '/api/img/2021052116215749528168197577.jpg', 51.00, '西餐', '原料：芦笋、土豆、精肉、胡萝卜、巴楚菌、食盐、鸡精。', '“小满吃点苦，一年心不苦。不知道有没有科学道理。不过因为妈妈出院了，我可以吃低脂低盐的瘦身食物啦，恰巧买了芦笋是苦的，就做个沙拉好了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (735, '芦笋豌豆鲜虾沙拉', '/api/img/2021052016214886149658197577.jpg', 33.00, '西餐', '原料：芦笋、豌豆、鲜虾、番茄、海盐、黑胡椒。', '“老娘住院，没时间弄着吃，买的豌豆放冰箱冷藏了半个月都发芽啦赶紧做了吃了，免得浪费。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (736, '边吃边瘦的健康美食～轻食蔬果虾仁沙拉', '/api/img/2021051916214285329188197577.jpg', 19.00, '西餐', '原料：黄瓜、大虾、冰草、鸡蛋、樱桃萝卜、葡萄、小番茄、荷兰豆、柠檬、洋葱、白芝麻、橄榄油、黑胡椒粉、蜂蜜、12克轻盐昆布酱油、料酒、胡椒粉。', '“夏天，很多爱美女性，纠结在又想吃东西，可是吃了又怕胖！明明最近很自律，一直在吃轻食美食沙拉，可体重就是减不下来，不知道该怎么办？其实你第一步就做错啦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (737, '爱尔兰牛排', '/api/img/2021051916214054456041958079.jpg', 20.00, '西餐', '原料：新西兰牛西冷谷饲、黄油卷、黄洋葱、大蒜、欧芹、盐和胡椒粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (738, '简单快手不用和面也能做出好吃的披萨', '/api/img/2021051516210545877408197577.jpg', 54.00, '西餐', '原料：爸爸糖鲜吐司、鸡蛋、蔬菜粒、芝士碎、牛奶、香肠、番茄酱。', '“你还在为不会制作披萨饼皮而烦恼吗？今天教大家不用和面做饼皮也能做出好吃的披萨哦！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (739, '沙拉', '/api/img/2021051516210504354268197577.jpg', 37.00, '西餐', '原料：西兰花、鸡胸肉、圣女果、千岛酱。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (740, '自制炸薯条', '/api/img/2021051416209787831358197577.jpg', 22.00, '西餐', '原料：土豆、玉米淀粉、食用油、盐，、番茄酱。', '“炸薯条是孩子门的最爱，看到超市新土豆上市就买了些自己回来做，很简单哟。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (741, '咸蛋黄米饭三文治', '/api/img/2021051416209720248898197577.jpg', 18.00, '西餐', '原料：吐司、咸蛋黄、熟鸡丝、葱花、芝麻沙拉汁、盐。', '“用吐司片夹着米饭、咸蛋黄烤着吃是我最近几个星期一直想做的点心，不知道为什么却没做成，直至看到有狠粮优选精米申请，自己才恍然大悟，原来自己在等这个米，哈...狠粮优选精米煮成饭后每一颗都是香香滑滑的，有点嚼劲，很适合做填馅；咸蛋黄米饭三文治经烤制后表面脆脆的，里面的米饭很有存在感，加上粉糯的咸蛋黄，太好吃了” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (742, '松软玉米奶油饼', '/api/img/2021051416209587117198197577.jpg', 53.00, '西餐', '原料：淡奶油、鸡蛋、低筋粉、盐、糖粉、鲜酵母、煮熟的玉米粒、黄油。', '“涨发太好，只在冰箱里呆了一夜，面糊就涨了个盆满钵满，甚至逃溢。。。放在电饼铛里也不肯好好呆着，自顾自肆意生长蔓延，最后变成连体婴儿。两个鸡蛋让面饼变得金黄，并与奶油一起散发浓香。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (743, '牛肉和鸡肉法吉塔', '/api/img/2021051316208839434641958079.jpg', 37.00, '西餐', '原料：新西兰牛侧腹牛腩谷饲、鸡胸肉、小彩椒(绿色)(300克)、新鲜白蘑菇 (250克)、玉米饼、红洋葱、红灯笼椒、卡吉恩调味料、喼汁、总统酸奶油150克、新鲜香菜、酱油。', '“法吉塔-------经典的墨西哥菜，足不出户就能做的墨西哥美食，大家快来试一试吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (744, '小麦粗粮法棍', '/api/img/2021051316208778473178197577.jpg', 32.00, '西餐', '原料：小麦面粉、酸奶、盐、橄榄油、酵母粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (745, '奶油柠檬帕玛森干酪鸡', '/api/img/2021051316208716079731958079.jpg', 28.00, '西餐', '原料：特级帕玛森奶酪 (24个月）、鸡胸肉、中筋面粉、柠檬、伊斯尼AOP淡味黄油卷250克、菠菜、蒂安黑胡椒研磨瓶45克、古特拉特级初榨橄榄油500毫升、英国美顿海盐薄片250克、大蒜、奶油、鸡汤、新鲜罗勒。', '“奶油柠檬帕玛森干酪鸡综合了西餐中大家比较中意的奶香风味与酸甜口感，菜肴的色泽以及摆盘的风格实在是西式得不要不要的了，配上可乐一起食用，感觉自己在海边的咖啡屋享受人生呢 ：）” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (746, '黑芝麻流心馅', '/api/img/2021100616334979886148197577.jpg', 44.00, '烘焙', '原料：黑芝麻、猪油、清水、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (747, '开口酥', '/api/img/2021100616334979454668197577.jpg', 46.00, '烘焙', '原料：中筋面粉、猪油、砂糖、水、低筋面粉、猪油、椰蓉、糖粉、奶粉、黄油、全蛋液、葡萄干、红曲粉、紫薯粉、抹茶粉。', '“开口酥是一个中式点心，此配方可以做12个不同颜色的开口酥。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (748, '早餐必备，松软香甜超好吃的奶香排包！', '/api/img/2021100616334840285758197577.jpg', 56.00, '烘焙', '原料：日式吐司面包粉、奶粉、鸡蛋、淡奶油、炼乳、牛奶、奶油奶酪、细砂糖、黄油、耐高糖酵母、盐。', '“添加了淡奶油跟奶油奶酪的奶香排包，柔软香甜，直接撕着吃，真真的老好吃了！这款奶香排包做法也不是很难，非常适合烘焙新手，准备好材料跟我做起来吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (749, '紫山药桃花酥', '/api/img/2021100516334005802831958079.jpg', 52.00, '烘焙', '原料：紫山药、手抓饼、蛋黄、黑芝麻、炼乳。', '“网上看的，觉得挺好的，就来山寨一下。人家用的紫薯，我没有紫薯，就用的紫山药。紫山药颜色更美丽一些，但是不容易弄成泥，是有颗粒感的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (750, '莓果蛋糕，一抹令人迷醉的紫色甜蜜', '/api/img/2021100416333440051228197577.jpg', 46.00, '烘焙', '原料：低筋面粉、泡打粉、砂糖、鸡蛋、淡奶油、黄油、甜奶油、蓝莓酱、蓝莓。', '“精致可爱的莓果蛋糕，色泽金黄均匀，淋上酸甜诱人的蓝莓果酱，放上新鲜蓝莓，与柔滑细腻的奶油碰撞出和谐的美味旋律。细细品尝，蛋糕胚温润绵密，口感轻盈细软，混合蓝莓的微酸和芳香，清新怡人的甜蜜在唇齿间流转，使人身心都沉醉于这一曲美味的交响乐舞曲中。搭配一杯花茶，或咖啡，优雅地享受惬意的下午茶时光。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (751, '煎蛋芝士大汉堡', '/api/img/2021100316332521698251958079.jpg', 41.00, '烘焙', '原料：高筋面粉、干酵母、白糖、盐、纯牛奶、黄油、表面刷牛奶、表面撒黑芝麻、鸡蛋、芝士片、生菜、午餐肉、番茄沙司。', '“今天想做汉堡，我和老头儿一人一个，因为用量少，直接手揉面，就发酵、烘烤上了。烤好的面包还挺暄软的，剖开之后就铺上了各种食材， 完美早餐齐活！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (752, '玛格丽特饼干', '/api/img/2021100216331530817338197577.jpg', 21.00, '烘焙', '原料：黄油、玉米淀粉、低筋粉、糖粉、熟蛋黄。', '“这是一个入门级的小饼干。名字听起来很华丽，但却很简单。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (753, '波兰种面包', '/api/img/2021100216331387733918197577.jpg', 53.00, '烘焙', '原料：高筋粉、波兰种、盐、糖、牛奶、奶粉、高筋粉、牛奶、耐高糖酵母、耐高糖酵母、黄油。', '“今天做了一个波兰种面包，需要的话看一下真的超香😘😘” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (754, '椰香奶黄馅', '/api/img/2021100216331386887098197577.jpg', 42.00, '烘焙', '原料：鸡蛋、白砂糖、椰浆、咖啡液、黄油、奶粉、低筋面粉、椰子粉、糯米粉、粘米粉、澄粉、白砂糖、牛奶、玉米油、斑斓粉、无。', '“其实这个菜谱是推这个馅料的，这个馅料椰香味十足。我用这个馅料做了斑斓冰皮的月饼，也可以用来做其他，大家可以自由发挥。如果做冰皮月饼，冰皮可以参考菜谱的配方” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (755, '红丝绒香缇奶油蛋糕卷', '/api/img/2021100116330712645051958079.jpg', 45.00, '烘焙', '原料：牛奶、色拉油、细砂糖1、红曲米、低筋粉、玉米淀粉、蛋黄、蛋白、细砂糖2、食盐、柠檬汁、马斯卡彭、淡奶油、细砂糖、香草膏。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (756, '荷兰樱桃磅蛋糕，蛋糕王国中的“始祖”，经典美味流传至今', '/api/img/2021100116330596527851958079.jpg', 56.00, '烘焙', '原料：无盐黄油、细砂糖、鸡蛋、低筋面粉、泡打粉、糖水樱桃。', '“荷兰樱桃磅蛋糕，在雪白绵密的奶油和鲜艳欲滴的樱桃点缀下，显得格外优雅美丽。仿佛一位身穿白色雪纺连衣裙，手捧刚摘下来的樱桃的少女在树下露出甜甜的笑脸。加入了樱桃的磅蛋糕，稍带酸甜的滋味中和了黄油的甜腻。蛋糕口感细腻却不失轻盈，丝毫没有重油蛋糕的厚实，轻咬一口，略带弹性，又绵密润滑得入口即化。与顺滑绵软的奶油完美融合，香甜得让人食指大动。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (757, '海绵蛋糕', '/api/img/2021093016329656976168197577.jpg', 43.00, '烘焙', '原料：鸡蛋、低粉、白砂糖、玉米油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (758, '椰香吐司（一次发酵）', '/api/img/2021092916328860311158197577.jpg', 29.00, '烘焙', '原料：高筋面粉、鸡蛋2个+椰汁、白糖、酵母、盐、玉米油、玉米油、椰蓉、白糖、鸡蛋、鸡蛋液（或蜂蜜水、牛奶）。', '“本款吐司用椰汁揉面，卷入椰蓉馅，椰香浓郁，蓬松柔软，皮薄拉丝，营养美味。采用冷藏泡面（静置），面团经过较长时间的静置，自然形成面筋，纯手工揉面也能很快揉出手套膜，极大缩短揉面时间，同时，采用一次发酵，省时省力，值得一试。本配方为二个450克吐司的用量。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (759, '菠萝蜜果肉戚风蛋糕', '/api/img/2021092916328791985158197577.jpg', 32.00, '烘焙', '原料：低粉、鸡蛋、玉米油、波萝蜜果肉、牛奶、白砂糖、白醋。', '“朋友送的大菠萝蜜，想了各种办法也吃不完，想想如果做成蛋糕，应该很好吃吧。于是翻看各种菜谱，也没找到用菠萝蜜果肉做的蛋糕， 只好改改戚风的方子试着做了，形象差些，囗味口感却非常好，果香浓郁。下次还有待于调试。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (760, '奥利奥布朗尼，烘焙界中最甜蜜的“误会”', '/api/img/2021092816328307031028197577.jpg', 49.00, '烘焙', '原料：无盐黄油、细砂糖、鸡蛋、低筋面粉、巧克力、奥利奥饼干。', '“在烘焙界里常有许多“甜蜜的误会”，这些不知不觉的无心之失却带来了许多令人惊喜的美味。而布朗尼蛋糕，便是其中一个“可爱的错误”。布朗尼也叫巧克力布朗尼蛋糕、核桃布朗尼蛋糕。由此可见，巧克力或者坚果是布朗尼蛋糕的“标配”。奥利奥布朗尼，在传统布朗尼中加入了饼干，让它的口感层次更丰富。泛着古铜棕色光泽的布朗尼蛋糕，奥利奥饼干像一个个小金币镶嵌在上面，颇有贵族的奢华风范。” 					', NULL, NULL, 1);
