INSERT INTO `foods` VALUES (797, '红豆沙馅', '/api/img/2021091616317563535598197577.jpg', 41.00, '烘焙', '原料：红豆、红糖、油、花生、水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (798, '蛋黃酥', '/api/img/2021091516316995155368197577.jpg', 30.00, '烘焙', '原料：酥油、無水奶油、中筋麵粉、低筋麵粉、細砂糖、水、豆沙餡、鹹蛋黃。', '“中秋節即將到來~特地做了蛋黃酥應景。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (799, '栗蓉蔓越莓腊肉月饼', '/api/img/2021091316314971089808197577.jpg', 15.00, '烘焙', '原料：奶香栗蓉馅、冬瓜蓉、腊肉、蔓越莓、低筋粉、转化糖浆、花生油、碱水、蛋液', '“初试果干配腊肉的效果似乎不错，换成蔓越莓再来一次，想象中，蔓越莓的酸甜应该会比较解肉的腻，结果好像还行吧，蔓越莓似乎可以再多些。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (800, '杏干腊肉月饼', '/api/img/2021091216314136251258197577.jpg', 36.00, '烘焙', '原料：红豆沙馅、冬瓜蓉、腊肉、芝麻、杏干、低筋粉、转化糖浆、花生油、碱水、蛋液。', '“纯粹为了消耗腊肉，尽可能多的让腊肉填充到馅料里面，又恐单纯的腊肉让人感觉厌倦，眼前的杏干一下子叫人豁然洞开果然，略有些酸甜的杏干不仅让腊肉月饼的口味更加丰富，简单粗暴的肉感也变得婉转许多。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (801, '广式豆沙月饼', '/api/img/2021091216314114915188197577.jpg', 45.00, '烘焙', '原料：普通面粉、转化糖浆、枧水、玉米油、奶粉、红豆、纯净水、玉米油、白糖。', '“冷置了10年之久的烤箱开始工作了。很多时候，我们缺少的就是动手的勇气。终于下定决心做月饼。第一炉月饼还算满意，遗憾的是回油后的月饼有点软，不知道问题出在哪里。望过路高手指点一二，先谢谢了🙏！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (802, '豆沙馅小面包', '/api/img/2021090716309788421561958079.jpg', 35.00, '烘焙', '原料：面粉、豆沙、酵母粉、玉米油、白糖、纯净水。', '“烘烤带馅的面包一是变换一下品种，二是可以改变口味。面包的馅料可以按照自己的喜欢添加，比如果酱、豆沙、枣泥、肉松等都可以，只要自己喜欢的都可以试一试。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (803, '冬蓉', '/api/img/2021090616308921994348197577.jpg', 38.00, '烘焙', '原料：冬瓜、冰糖、麦芽糖、玉米油。', '“喜欢冬蓉月饼，今年找遍各华人超市，居然没冬蓉月饼卖，没办法，只好自己做了。自家种的大冬瓜，一直舍不得摘，结果自个掉了下来，破相了，正好用来做冬蓉。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (804, '黑芝麻酱豆沙月饼', '/api/img/2021090516308291248321958079.jpg', 39.00, '烘焙', '原料：中筋面粉、绿豆沙、转化糖浆、玉米油、枧水、黑芝麻酱、表面刷蛋黄液。', '“最近月饼真是做“嗨”了，300克的脱皮绿豆居然炒出一大盆豆沙来。每种月饼我只做6块，居然还有富余。绞尽脑汁再想个新花样吧，得，就它了，黑芝麻酱豆沙月饼，还别说，歪打正着，还真好吃。100%的纯黑芝麻酱，不但香气扑鼻，入口也非常香醇。黑芝麻的营养价值也高，富含优质蛋白质、钙、不饱和脂肪酸、维生素E等营养物质。每次不宜多，有10克左右就足够了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (805, '猪肉汉堡包（一次发酵）', '/api/img/2021090516308067062321958079.jpg', 31.00, '烘焙', '原料：高筋面粉、牛奶、奶粉、白糖、酵母、鸡蛋、玉米油、鸡蛋液、白芝麻、生菜、西红柿、沙拉酱、猪里脊肉末、盐、生抽、玉米淀粉、黑胡椒粉、料酒、洋葱。', '“汉堡包是英语Hamburger的音译，是一种发源于德国汉堡的食品，是现代西式快餐中的主要食物，它是由一切二半的面包中间夹入经处理熟的牛肉饼，上面撒上各种调味粉、沙拉、香菜等而成，后来又发展成鸡柳汉堡、鳕鱼汉堡，牛肉汉堡、猪肉汉堡……。本款猪肉汉堡胚采用冷藏泡面、一次发酵制作，操作简单，省时省力，此外，肉饼除了有洋葱肉饼外，还有香芋肉饼、胡萝卜肉饼，营养美味，口感丰富，每一层都有不同的味道，让人回味无穷。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (806, '奶黄流心月饼', '/api/img/2021090316306777176668197577.jpg', 51.00, '烘焙', '原料：咸蛋黄、淡奶油、白糖、椰浆、淀粉、吉士粉、吉利丁、奶粉或炼奶、鸡蛋、白糖、奶粉或是炼奶、淡奶油、低筋面粉、咸蛋黄、低筋面粉、转换糖醇、食用油、枧水。', '“奶黄流心月饼，月饼中的霸者，传统中的流心月饼那是卡路里炸弹，加了好多的黄油和淡奶油，我家宝宝喜欢吃，但是他比较不喜欢涨肉肉，所以我在能精简不影响口感的情况下做了一丢丢的改变，没想到流心还是棒棒哒👍（这次的月饼是63克模具20个）” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (807, '料足味香的五仁月饼', '/api/img/2021090316306413991728197577.jpg', 59.00, '烘焙', '原料：中筋面粉、花生碎、黑白芝麻、南瓜籽、蔓越莓干、馅料用中筋面粉、转化糖浆、玉米油、枧水、麦芽糖、蛋黄液。', '“今年特别流行的花生月饼造型，我也潮了一把。五仁馅是自制的，真材实料。皮子和馅料的比例是2：8，手艺是没得挑，但因为皮软馅硬，所以有的地方看着像破皮儿了。虽说是烤月饼，简直可以说是烤五仁了。哈哈，但是成品真的超好吃，非常香！想要这个馅的月饼外皮光滑漂亮看不到内馅，可以把皮馅的比例调整为3：7，这样就不会有“吹弹可破”之感了。传统的广式月饼外皮上要刷一层蛋黄液，回油后显得油润润的，色泽也漂亮。想要花生造型的外观颜色统一，可以不刷蛋黄，烤时加盖锡纸可避免颜色分布不均。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (808, '龙利鱼汉堡', '/api/img/2021090316306336178178197577.jpg', 15.00, '烘焙', '原料：面包、龙利鱼、火腿肠、鸡蛋、生菜叶、玉米粒、胡萝卜、葱、姜、蒜、料酒、生抽、蚝油、冠利酸甜青瓜蓉汉堡酱。', '“面包里加它，低脂饱腹，能量满满。开学季，早餐不能忽视，鸡蛋牛奶不能少，多吃蔬菜水果，推荐今日份早餐，面包与生菜、玉米粒、胡萝卜、龙利鱼、搭配，好吃又简单，重要的是低脂低卡，吃着瘦的减脂餐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (809, '花生酱毛线球面包（一次发酵）', '/api/img/2021090316306329736641958079.jpg', 43.00, '烘焙', '原料：高筋面粉、牛奶、奶粉、鸡蛋、白糖、酵母、盐、玉米油、花生酱、蜂蜜水。', '“外表像团毛线，毛绒绒，超可爱，不用揉出手套膜也可做出柔软拉丝、香味浓郁的毛线球面包，而且，采用冷藏泡面，面团经过一夜的冷藏静置，自然出筋，直接用手就可拉开一张有点厚度、非常有韧性的大膜，比普通制作方法至少节省了一半的揉面时间，省时省力，特别适合上班族。本配方为一个8寸活底蛋糕圆模的用量。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (810, '芒果雪梨慕斯', '/api/img/2021090216305890306001958079.jpg', 46.00, '烘焙', '原料：消化饼干、黄油、白凉粉、芒果、巴旦木仁饮料、红石榴汁、梨。', '“周末简单下午茶——芒果雪梨慕斯” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (811, '简单制作蔓越莓司康饼，适合新手', '/api/img/2021090216305765105531958079.jpg', 49.00, '烘焙', '原料：司康饼搅拌粉、黄油、牛奶、鸡蛋。', '“司康饼松软香脆，非常可口，如果在制作的时候加入自己喜欢爱的蓝莓、葡萄干或者蔓越莓干，味道就更好吃了。但以前做司康饼总需要很多配料，新手做起来比较麻烦，最近我发现了司康饼搅拌粉，制作简单适合新手。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (812, '玛格丽特饼干', '/api/img/2021090216305551846238197577.jpg', 52.00, '烘焙', '原料：低粉、玉米淀粉、熟蛋黄、黄油、奶粉、糖、盐。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (813, '经典莲蓉月饼', '/api/img/2021090116304834633298197577.jpg', 19.00, '烘焙', '原料：中筋面粉、莲蓉馅、转化糖浆、玉米油、枧水、蛋黄液。', '“传统的圆形月饼虽然有团团圆圆的寓意在，但是形状已经缺少新意了，好多有形有寓意的模具随之而生，比如今天的这个藕芋模具真是太逼真了。此季也正是莲藕大量上市之时，用莲蓉做内馅，内外统一，很有趣。以往我都自己炒馅，这次偷懒了，用了大品牌莲蓉馅，低油低糖还不错。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (814, '黑全麦广式腊肠面包卷', '/api/img/2021090116304629042748197577.jpg', 45.00, '烘焙', '原料：高筋粉、黑全麦粉、鲜酵母、白砂糖、盐、牛奶、水、鸡蛋、黄油、广式香肠（蒸熟切碎）。', '“广式腊肠蒸熟切碎，卷进黑全麦的面团里藏不住的小肉丁在卷卷两端探头探脑看起来就肉香满满。。。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (815, '面包控不可错过的一款小面包，香味十足巨好吃！', '/api/img/2021090116304601154928197577.jpg', 44.00, '烘焙', '原料：日式吐司面包粉、低筋面粉、奶粉、牛奶、淡奶油、炼乳、细砂糖、黄油、耐高糖酵母、有机黑芝麻酱、白芝麻。', '“香味超浓郁的黑芝麻小面包，柔软香甜，造型简单，学会了早餐再也不用愁了！两股辫子扭一下，塞进模具，造型别提多简单了，手残党都不用怕，操作零难度，颜值也的说的过去的！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (816, '脏脏麻薯布丁塔', '/api/img/2021083116303970277771958079.jpg', 52.00, '烘焙', '原料：黄油、糖粉、鸡蛋、低粉、糯米粉、糖、牛奶、可可粉、黄油、淡奶油、炼乳、蛋黄、可可粉、巧克力脆珠。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (817, '珍妮曲奇', '/api/img/2021083116303969296201958079.jpg', 43.00, '烘焙', '原料：黄油、糖粉、盐、高粉、低粉、玉米淀粉、扁桃仁酱、香草精华。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (818, '培根船面包卷', '/api/img/2021083116303959073761958079.jpg', 41.00, '烘焙', '原料：高粉、开水、白砂糖、新鲜酵母、黄油、干燥葱、沙拉酱、光亮剂。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (819, '家常老婆饼，外皮金黄诱人，内里酥松，每一口都尝得到香甜滋味！', '/api/img/2021082916302292919188197577.jpg', 47.00, '烘焙', '原料：中筋面粉、白砂糖、清水、无盐黄油、中筋面粉、无盐黄油、糯米粉、白芝麻、椰蓉、白砂糖、清水、无盐黄油、蛋黄、白芝麻。', '“老婆饼源自广东潮州，是当地酒楼的招牌点心，外皮金黄诱人，内里油酥薄如棉纸，酥松得不得了， 一咬下去碎屑便掉了满地，每一口都尝得到蜜糖般的香甜滋味！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (820, '五仁月饼', '/api/img/2021082916302259158628197577.jpg', 51.00, '烘焙', '原料：低筋面粉、转换糖浆、食用油、枧水、紫薯馅、五仁馅。', '“中秋节，是中国民间的传统节日。中秋节源自天象崇拜，由上古时代秋夕祭月演变而来。中秋节自古便吃月饼、赏花灯的民俗，流传至今，经久不息。下面的配方是75克月饼20个，饼皮是25克的配方。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (821, '广式鸭润腊肠面包卷', '/api/img/2021082916302060157588197577.jpg', 49.00, '烘焙', '原料：高筋粉、蛋黄、白砂糖、盐、鲜酵母、鲜牛奶、黄油、广式鸭润腊肠（蒸熟）。', '“一包广式鸭润腊肠，看起来黑乎乎不太讨喜卷进面团里，尝尝不一样的腊肠面包卷黄灿灿的蛋黄把面团染成金色，掩盖了黑乎乎的肠最终发酵完成的面包卷齐刷刷地向左旋转了小30度，成斜向排放，真有意思” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (822, '莜麦花瓣面包', '/api/img/2021082516298957251148197577.jpg', 31.00, '烘焙', '原料：高筋面粉、莜麦面粉、鸡蛋、即发高活性干酵母、白砂糖、盐、核桃油、高筋面粉、莜麦面粉、白砂糖。', '“　　弹性十足的小面包，加了莜麦面粉，又好吃又健康；这款面包的造型也很独特，外皮象绽放的花瓣，就叫它莜麦花瓣面包吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (823, '红曲蛋糕卷', '/api/img/2021082516298810719651958079.jpg', 52.00, '烘焙', '原料：鸡蛋、低筋粉、红曲粉、牛奶、白糖、玉米油、苹果酱。', '“红曲是红曲霉素生粳米上生成的红曲米，颜色靓丽，不仅是一种天然色素，还可以活血化瘀、健脾消食。用红曲做蛋糕卷，自制的苹果酱夹馅，好看又好吃！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (824, ' 摩卡奶酥面包', '/api/img/2021082416298086643131958079.jpg', 38.00, '烘焙', '原料：高筋面粉、全蛋液、水、盐、干酵母、细砂糖、速溶咖啡粉、黄油、黄油、糖粉、可可粉、奶粉、全蛋液、盐、全蛋液、奥利奥碎', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (825, '饼干~', '/api/img/2021082416298004758361958079.jpg', 27.00, '烘焙', '原料：黄油、鸡蛋黄、低筋面粉、奶粉。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (826, '芝麻豆沙酥饼，制作简单，层层酥脆，一口下去直掉渣，香酥可口。', '/api/img/2021082416297958226018197577.jpg', 35.00, '烘焙', '原料：普通面粉、开水、室温水、普通面粉（油酥原料用）、植物油、泡打粉、豆沙馅、清水、面粉、白芝麻。', '“今天做一款经典的传统小吃-芝麻豆沙酥饼。制作还算简单，成品可以说是香酥可口，层层酥脆，一口下去直掉渣，是老幼皆宜的小吃之一。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (827, '玉米葵花饼，健康粗粮法帅蒸烤箱怎么做？', '/api/img/2021082416297774346821958079.jpg', 34.00, '烘焙', '原料：玉米面、烧开的热水、普通面粉、蛋液、酵母、蛋黄。', '“本次推荐的是玉米葵花饼，浓郁的玉米香，加上漂亮的外形和香酥脆的口感，让人胃口大开。玉米面中富含钙、铁等元素，同时热量很低，食用后饱腹感强，是非常好的减肥食品。拒绝高油高糖，这款健康的粗粮面包很适合你。 本次使用机器是法帅蒸烤箱内置上下M型发热管+双环形背部发热管，配上背部大风机能够实现腔内的热量360度吹均匀，无论是你是上下烤、热风烤、蒸烤都能烤出漂亮的美食哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (828, '糖珠火山云朵☁️蛋糕', '/api/img/2021082316297236395428197577.jpg', 20.00, '烘焙', '原料：蛋白液、柠檬汁、糖粉、玉米淀粉、糖珠。', '“云朵蛋糕都是小土坡造型，今天突发奇想凹一个火山🌋造型，撒上糖珠，做出类似火山爆发的造型。喷的不是岩浆而是糖珠，出来的成品也挺漂亮的。有点小可爱哦😊” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (829, '无需打发的蒸小蛋糕，法帅蒸烤箱做法', '/api/img/2021082316297054355481958079.jpg', 24.00, '烘焙', '原料：普通面粉、鸡蛋、糖、柠檬汁、奶粉、玉米油、泡打粉。', '“在夏日不妨来吃点下火的东西，多吃蒸煮的食物，不仅没那么燥热，营养也保留得也相对好一点。 喜欢吃蛋糕的小伙伴，不妨来试试蒸蛋糕，吃起来还非常好吃哦，蒸的时间和烘烤的时间差不多，口感很湿润，又是一种感觉，但依旧好吃。 今天用的机器是蒸烤箱，法帅蒸烤箱采用蒸汽直喷的原理，4.0即热式蒸发盘能够确保进入的蒸汽是绵密细腻的，蒸制小蛋糕可以不用担心有冷凝水滴落，导致表面坑坑哇哇，四周软塌塌哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (830, '酒糟蛋糕', '/api/img/2021082316296919367151958079.jpg', 40.00, '烘焙', '原料：低筋面粉、鸡蛋、醪糟、食用油、泡打粉、牛奶、糖、柠檬汁、香草精。', '“家里酿了米酒剩了很多醪糟吃不完。蛋糕体内部很湿润柔软。酒糟味不浓，主要是口感很湿润。用来消耗醪糟。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (831, '韩式鸡蛋面包', '/api/img/2021082216296261001261958079.jpg', 15.00, '烘焙', '原料：低筋粉、水、鸡蛋、糖、泡打粉、盐、芝士片、黑椒香肠、肉松、黑胡椒碎、鸡蛋。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (832, '麻薯奶香吐司', '/api/img/2021082216295973657101958079.jpg', 32.00, '烘焙', '原料：高筋面粉、白鲨麻薯粉、奶粉、干酵母、全蛋液、牛奶、细砂糖、盐、黄油。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (833, '萨拉米甜辣面包卷', '/api/img/2021082116295164123068197577.jpg', 16.00, '烘焙', '原料：高筋粉、细砂糖、盐、全蛋、牛奶、鲜酵母、黄油、拌饭酱、萨拉米香肠。', '“看中一款萨拉米青酱面包卷，却没有青酱。手中一瓶拌饭酱，有点像甜辣酱的口味，索性用来代替青酱。酱料抹在面片上才知道原来这酱里还有些个花椒大小的颗粒，有点像肉肉。烘烤后这酱料似乎隐去了些许甜味，而辣味却变得明显” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (834, '椰香面包卷，做法简单，入口绵软细腻，椰香味十足，口感超好。', '/api/img/2021082016294437519631958079.jpg', 56.00, '烘焙', '原料：高筋面粉、白砂糖、盐、酵母、椰奶、清水、椰子油、玉米淀粉、热牛奶、抹茶粉、椰蓉。', '“今天这款椰香面包卷，刚出炉的时候超级香，做法简单，入口细腻，口感超好，底部的椰奶和面包融合在一起后，味道超级赞！！！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (835, '奶茶吐司', '/api/img/2021082016294413327561958079.jpg', 10.00, '烘焙', '原料：面包粉、奶茶、酵母粉、黄油、盐、鸡蛋、甜奶粉、纯净水。', '“在面粉中添加奶茶，口感层次多了，香香的很不错。利用超简单方便的面包机，一键完成。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (836, '麻薯欧包', '/api/img/2021082016294374829628197577.jpg', 53.00, '烘焙', '原料：高筋粉、白砂糖、盐、可可粉、酵母、鸡蛋、黄油、糯米粉、玉米淀粉、白砂糖、黄油、牛奶、水果丁、红枣丁、葡萄干', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (837, '全麦坚果欧包，法帅蒸烤箱怎么做？', '/api/img/2021082016294231935991958079.jpg', 48.00, '烘焙', '原料：高筋面粉、全麦粉、幼砂糖、无盐黄油、清水、耐高糖酵母、盐、核桃仁、南瓜子、蔓越莓干。', '“对于热爱健身，喜欢低糖低油饮食的小伙伴来说，今天给大家推荐的全麦坚果欧包就很适合了，里面添加了全麦粉，坚果和果干儿，外脆内软，真心好吃的停不下来。做法其实也很简单，厨房小白也可以很好掌握哦！自己做真材实料无添加，健康又美味。法帅蒸烤箱怎么做欧包呢？利用法帅蒸烤箱的1档可调发酵功能，上下独立控温的烧烤模式，能够做出外脆里嫩的欧包哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (838, '肉松芝麻香葱吐司~法帅蒸烤箱版', '/api/img/2021081916293616446128197577.jpg', 17.00, '烘焙', '原料：高筋面粉、幼砂糖、无盐黄油、耐高糖酵母、盐、鸡蛋1个+牛奶、肉松、沙拉酱、熟白芝麻、全蛋液、沙拉酱、番茄酱、香葱叶', '“小可爱们跟着小法做一款咸口的吐司吧！整形也变一下，有颜又有料的肉松芝麻香葱吐司就这样诞生啦！松软可拉丝哦，简直停不下口！法帅蒸汽烤箱自带发酵模式， 20-60度的广域控温，能够1度一调节，适合不同食材的发酵需求。上下M型发热管均可独立控温，能够根据上色需求进行温度的调节，烘焙效果更好哦~今天的配方可以做两个450g的吐司。超好吃的，不信你就试试呗~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (839, '纯奶手撕吐司（法帅蒸烤箱做法）', '/api/img/2021081916293524809551958079.jpg', 34.00, '烘焙', '原料：高筋面粉、糖、奶粉、酵母、盐、鸡蛋、黄油、牛奶。', '“这个食谱超简单，重点是只需要1发哦，没有其余的醒发，超级节省时间。面团会有那么一丢丢偏干，所以出膜比较慢，要注意面团温度哦！纯奶手撕吐司，答应小法要一条条撕着吃，超过瘾！这次使用的机器是法帅32L台嵌两用款，内置多种烹饪模式，三管发热+一组背部风机，双孔蒸汽直喷，无论蒸煮、烧烤抑或烘焙都能轻松实现哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (840, '一次发酵红豆沙小餐包，法帅蒸烤箱做法', '/api/img/2021081716291690858821958079.jpg', 42.00, '烘焙', '原料：面包用高筋粉、无盐黄油、盐、幼砂糖、牛奶、耐高糖鲜酵母。', '“大家都知道做面包很费时间，需要经过2次发酵，再加上整形烘烤，基本上半天就过去了。其实掌握好方法，一次发酵的小餐包也一样美味好吃，一起做起来吧！今天的红豆沙面包是没有鸡蛋的，鸡蛋过敏的小可爱们也可以吃哦！本次使用的机器是蒸烤箱，法帅蒸烤箱特有上下独立控温技术，上下管可分别设置不同的温度，控制烘焙的上色情况，专门为蒸烤烘焙量身订做的呢。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (841, '红枣戚风蛋糕', '/api/img/2021081716291642847471958079.jpg', 30.00, '烘焙', '原料：鸡蛋、红枣泥、白糖、低筋面粉、椰子油、白醋、盐、清水。', '“很早之前就想做个枣泥蛋糕了，一直没有动手，这次买了多一点的红枣，将红枣泡软后，切枣肉去核，打成枣泥。一切准备就绪后，就可以开奶做啦。做好的红枣蛋糕，浓浓的枣香味，真是迷人的香味呀，不吃闻着就香呢。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (842, '酸奶酥饼', '/api/img/2021081716291640926738197577.jpg', 19.00, '烘焙', '原料：低筋面粉、细砂糖、酸奶、黄油、盐、表面装饰、细砂糖。', '', NULL, NULL, 1);
