INSERT INTO `foods` VALUES (848, '拿铁中空蛋糕，FASAL法帅蒸烤箱制作', '/api/img/2021081316288234579061958079.jpg', 47.00, '烘焙', '原料：鸡蛋、玉米油、牛奶、糖（蛋黄）、糖（蛋白）、低粉、咖啡、咖啡液。', '“下面跟着小法做一件拿铁中空蛋糕，既可以享受到蛋糕的细腻香甜，也可以闻到扑鼻而来的咖啡香，超级赞的呢！在大视窗下看着蛋糕慢慢的膨胀起来，感觉到满满的成就感哦！一杯肥仔快乐奶茶加一份拿铁中空戚风蛋糕，开启美好的假日之旅~这次使用的机器是法帅32L台嵌两用款，内置多种烹饪模式，三管发热+一组背部风机，双孔蒸汽直喷，无论蒸煮、烧烤抑或烘焙都能轻松实现哦~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (849, '免揉欧包<法帅蒸汽烤箱式>', '/api/img/2019041515553087679229702111.jpg', 13.00, '烘焙', '原料：高筋面粉、干酵母、水、盐。', '“清晨伴随着和煦的阳光来一片沾满果酱的欧包一杯热乎乎的牛奶旁边还有炒蛋和沙拉享受美好的一天开始包少油、少糖、低热量的特点很符合当代人的健康养生观念欧包进烤炉时一定要有蒸汽支撑以形成脆硬的外壳好的欧包内部会有不规则的大气孔这是长时间发酵的结果只需要提前一个晚上把材料混合好放入冰箱冷藏一个晚上发酵第二天拿出来烤就好重点是免揉哦” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (850, '非油炸甜甜圈【法帅蒸汽烤箱版】', '/api/img/2019032615535854725809724956.jpg', 38.00, '烘焙', '原料：鸡蛋、牛奶、细砂糖、玉米油、巧克力币、低筋面粉、黄油、装饰糖。', '“甜食总有种独特的吸引魅力在纽约，有三大甜点一定要品尝一个就是可颂面包一个就是贝果面包一个就是从八岁到八十岁的人都一定会爱的甜甜圈” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (851, '经典菠萝包【法帅蒸汽烤箱式】', '/api/img/2019032515534785291459702111.jpg', 17.00, '烘焙', '原料：高筋面粉、细砂糖、全蛋液、干酵母、奶粉、牛奶、清水、无盐黄油、低筋面粉、无盐黄油、细砂糖、全蛋液、奶粉、蛋黄、无盐黄油、盐。', '“老广长大的孩子童年都会有一个菠萝包的记忆如今，面包的种类繁多十几年从未减少对它的喜爱。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (852, '健康无油酸奶吐司', '/api/img/2021081316288183913018197577.jpg', 20.00, '烘焙', '原料：高筋面粉、酸奶、白糖、酵母、三能450克吐司模具。', '“这是一款健康无油的酸奶吐司（又称之为优格吐司）。酸奶可用来做天然酵种，可以帮助面团发酵，可使面团变得更加柔软，面团成熟之后酸奶成分可防止面包老化和改良的作用。我强烈推荐这一款酸奶吐司。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (853, '红曲蛋糕卷', '/api/img/2021081216287587032651958079.jpg', 34.00, '烘焙', '原料：蛋奶油、鸡蛋、玉米油、牛奶、红曲粉、白糖、低粉、醋。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (854, '原味蛋糕卷', '/api/img/2021081216287575823671958079.jpg', 21.00, '烘焙', '原料：鸡蛋、牛奶、玉米油、白糖、低粉、醋。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (855, '蓝莓芝士迷你磅蛋糕', '/api/img/2021081216287321156938197577.jpg', 16.00, '烘焙', '原料：黄油、奶油奶酪、白砂糖、鸡蛋、低粉、泡打粉、蓝莓。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (856, ' 巧克力乳酪蛋糕', '/api/img/2021081116286725961348197577.jpg', 23.00, '烘焙', '原料：鸡蛋、奶油奶酪、巧克力、低筋粉、绵白糖、可可粉、牛奶、玉米油、朗姆酒。', '“巧克力是大多人无法抗拒的，乳酪蛋糕也是深受人们喜爱，巧克力与乳酪相结合做成蛋糕，香甜绵软，个中美味谁吃谁知道！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (857, '儿童餐这样做，孩子抢着吃！', '/api/img/2021081116286626869771958079.jpg', 23.00, '烘焙', '原料：爸爸糖鲜吐司、肉松、芝士、沙拉酱、巧克力酱。', '“儿童餐这样做孩子抢着吃，萌萌哒小狮子肉松小贝，充满童趣，做早餐或者饭后甜点都不错哦~国民早餐计划一起来get吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (858, '黄桃蛋挞', '/api/img/2021081116286499319678197577.jpg', 34.00, '烘焙', '原料：黄桃罐头、蛋挞皮206、鸡蛋、奶油、炼乳、牛奶、白糖。', '“休假在家，天气总是下着小雨。见家里的储物柜和冰箱里还有些蛋挞皮，就做起了黄桃蛋挞。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (859, '斑斓椰蓉小吐司，风味独特颜色美美哒！', '/api/img/2021081016286019377901958079.jpg', 11.00, '烘焙', '原料：吐司粉、奶粉、斑斓粉、淡奶油、蛋白液、牛奶、炼乳、细砂糖、耐高糖酵母、盐、海苔肉松、沙拉酱、椰蓉。', '“一款内容超级丰富的小吐司，颜值、口感都满分！早餐绝对优选！内馅选择也多多，豆沙、肉松、花生酱等等，都很好吃的呢！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (860, '混搭的意式奶油包', '/api/img/2021081016285815363268197577.jpg', 28.00, '烘焙', '原料：高粉、牛奶、白砂糖、鸡蛋、盐、新鲜酵母、黄油、马斯卡朋奶酪、淡奶油、白砂糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (861, '天使可可奶油卷', '/api/img/2021081016285809154161958079.jpg', 22.00, '烘焙', '原料：蛋白、牛奶、玉米油、玉米淀粉、低粉、奶粉、白砂糖、淡奶油、可可粉、白砂糖、柠檬汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (862, '紫薯蔓越莓牛牛吐司', '/api/img/2021080816284259185301958079.jpg', 32.00, '烘焙', '原料：吐司粉、紫薯、全蛋液、淡奶油、牛奶、细砂糖、黄油、耐高糖酵母、盐。', '“紫薯蔓越莓的搭配，健康、营养、美味，而萌萌的牛牛造型给这款小吐司格外的加分！那嫩嫩的颜色，绝对让人眼前一亮！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (863, '咖喱鸡肉彩色酥饼', '/api/img/2021080816284058295971958079.jpg', 38.00, '烘焙', '原料：鸡琵琶腿、土豆、洋葱、葱姜、百梦多原味咖喱、料酒、盐糖、干淀粉、胡麻油、中筋面粉、斑澜粉、南瓜粉、紫薯粉、开水、胡麻油、中筋面粉。', '“　　家中常备百梦多的块状咖喱，但是老吃咖喱饭已经有点腻了，上次我就做了咖喱面包鸡，这次突发奇想要做酥饼吃。　　酥饼好吃，但用油量也惊人。用猪油做酥饼是最理想的，但我害怕胆固醇太高，所以这次用的是植物油——胡麻油。胡麻油是以胡麻籽为原料通过传统的榨油技术得到的食用油它不但风味独特而且还有强身、健体、美容、廋身等功效。　　胡麻油的起酥效果也相当好，做好的酥饼真是酥得掉渣。且咖喱鸡肉馅，也给人新奇的感受。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (864, '肠仔芥末法棍面包', '/api/img/2021080816283899562561958079.jpg', 38.00, '烘焙', '原料：高筋粉、细砂糖、盐、鲜酵母、水、芥末酱、手掰蒜肠。', '“很早看中的一款肠仔芥末法棍面包，正好可以用这长长的手撕蒜肠来试试。弯曲的肠不能做成长长的一根长棍面包，但是可以切掉弯曲的部分做成小棍面包。原方说，“这个配方的面团，可以做五根长35cm左右的肠仔包。15cm的肠仔可以做十根。”那么，就取半份量，切两段15厘米的肠再纵切开，正好可以做4根短棍。包得很丑陋，割包也很丑，唯一可以安慰的是，烤出来很香。忽略掉外形，也算是一款不错的肉肉面包。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (865, '砂糖黄油饼干，超酥松香脆，制作简单，下午茶好搭档。', '/api/img/2021080716283068609488197577.jpg', 43.00, '烘焙', '原料：无盐黄油、糖粉、蛋黄、蛋清、低筋面粉、可可粉、抹茶粉、白砂糖。', '“这款砂糖黄油饼干，制作非常简单，入口酥松香浓，在悠闲的午后，搭配一杯咖啡，来一口黄油饼干，超级惬意。” 					', NULL, NULL, 1);
