INSERT INTO `foods` VALUES (927, '健脾芝麻糊', '/api/img/2021082516298754623468197577.jpg', 18.00, '饮品', '原料：黑芝麻、红枣、山药、糯米、冰糖、水。', '“每天一杯淮山黑芝麻糊，健脾胃，吃多了也不会发胖。保健作用好。尤其是中年白发者，皮肤皱纹增多者，坚持一段时间，效果会相当不错！原料：黑芝麻100克、糯米50克、山药100克、红枣9粒配料：冰糖25克、水适量” 					', NULL, NULL, 1);
