INSERT INTO `foods` VALUES (932, '酸甜桔子汁', '/api/img/2021082016294442633188197577.jpg', 55.00, '饮品', '原料：桔子、无、无。', '“现在桔子不是很甜，是酸酸甜甜的口感，打成汁加冰块，赞～爽。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (933, '江中猴姑米稀', '/api/img/2021082016294435187728197577.jpg', 27.00, '饮品', '原料：江中猴姑米稀、开水。', '“女儿特意买给我们的江中猴姑米稀，说是养胃挺好，主要原料为人参、砂仁、山药、茯苓、莲子、白扁豆、薏苡仁等药食两用材料，小小一包，价钱不菲。感念小棉袄的浓浓爱意，今天就泡了一碗当早餐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (934, '果茶之桃之夭夭', '/api/img/2021082016294375871778197577.jpg', 12.00, '饮品', '原料：桃子、鲜牛乳、白凉粉、纯净水、白糖。', '“抓住夏天的尾巴，趁着桃子🍑够大够红自己在家复刻奶茶店的“桃之夭夭”吧！” 					', NULL, NULL, 1);
