INSERT INTO `foods` VALUES (941, '珍珠奶茶', '/api/img/2021081716291706871781958079.jpg', 30.00, '饮品', '原料：木薯淀粉、红茶、红糖、可可粉、牛奶。', '“珍珠奶茶大家都喜欢喝，可是好多爱漂亮爱美的朋友都不想喝，为什么呢？因为外面的太多的添加剂，怕涨肉肉，可是自己做又觉得搓珍珠麻烦，其实并没有大家想的那么难，不信？那你往下看👇” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (942, '杨枝甘露', '/api/img/2021081616291197742161958079.jpg', 50.00, '饮品', '原料：芒果、红西柚、酸奶、椰汁、西米。', '“杨枝甘露的配料，简单明了，可是，做出来味道总是不太如意，纯果肉的果浆有些腻，只放椰汁又有点淡，“鲜果时间”的杨枝甘露是怎么做的嘞？前天晚上去万象城看电影，买了一杯杨枝甘露，没有人排队，于是……认真观察了一下，“鲜果时间”的做法。今天再次解锁，味道果然超棒。分享给大家。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (943, '懒人版阿芙佳朵', '/api/img/2021081616291190764851958079.jpg', 33.00, '饮品', '原料：手冲咖啡、开水、光明冰砖、淡奶油、糖粉。', '“　　这是一款源自意大利的咖啡甜点——Affogato，翻译过来的意思是“淹没”，形容被香醇咖啡淹没的冰淇淋，是让人觉得美味到窒息的食物。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (944, '清爽一夏的百香姜梨有机醋饮', '/api/img/2021081616291189347241958079.jpg', 13.00, '饮品', '原料：姜、梨、百香果、禾然有机糙米醋、气泡水、蜂蜜。', '“三伏天湿气较重，夏日宜食姜，取姜和梨肉打成汁，再搭配有机糙米醋和气泡水，清爽一夏的百香姜梨有机醋饮就搞定了。简单易做，不仅能去湿气，还有软化血管等功效。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (945, '浪漫玫瑰鸡尾酒有机醋饮', '/api/img/2021081616291090994748197577.jpg', 35.00, '饮品', '原料：玫瑰花、冰糖、禾然有机糙米醋、干邑、气泡水。', '“玫瑰永远是七夕永恒的主题之一，玫瑰、冰糖入有机醋封存，让玫瑰的香气渗透在有机醋里，再调制成玫瑰鸡尾酒有机醋饮，让微醺浪漫的日常生活，多一些酸甜，少一些苦楚。” 					', NULL, NULL, 1);
