INSERT INTO `foods` VALUES (947, '蜜枣红薯豆浆', '/api/img/2021081616290778323308197577.jpg', 50.00, '饮品', '原料：黄豆及鹰嘴豆、红薯、酸奶麦片、蜜枣。', '“最近几天，尽管中午还是挺热，一早一晚很是凉爽，有点秋高气爽的味道了。可以喝热饮啦。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (948, '绿峰有礼', '/api/img/2021081516290178175968197577.jpg', 48.00, '饮品', '原料：巨峰葡萄、白凉粉、脱脂牛奶、雪碧、纯净水、冰糖。', '“网上学来的饮品，味道超好的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (949, '南瓜腰果露', '/api/img/2021081516289983703248197577.jpg', 22.00, '饮品', '原料：南瓜、原味烘焙腰果、纯净水。', '“昨天外食，在西餐厅点了一个南瓜腰果露，甜的。味道很不错口感也很好香香浓浓，今天试在自己在家做一份看看，食材都是容易买到的，做法也简单，用破壁机更方便。” 					', NULL, NULL, 1);
