INSERT INTO `foods` VALUES (958, '蜂蜜腌香水柠檬', '/api/img/2021080716283247349418197577.jpg', 59.00, '饮品', '原料：香水柠檬、食盐、蜂蜜。', '“无籽的香水柠檬一斤要十几块，买三斤的性价比最合适。吃不完，就腌起来，用来泡水喝。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (959, '苹果酸奶奶昔 ', '/api/img/2021080716283245853618197577.jpg', 30.00, '饮品', '原料：苹果、酸奶、凉开水。', '“不去买饮料，不去买奶茶，自己在家就能做，暑天的健康果饮 。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (960, '洛神花酸梅汤', '/api/img/2021080716283041233938197577.jpg', 48.00, '饮品', '原料：乌梅、山楂干、洛神花、水、冰糖。', '“把各种配比的酸梅汤都煮来喝一遍，大概就能找出一款相对满意的味道。而这一道只有简单三种料的酸梅汤看起来特别省事省时，煮20分钟就行。不过，后来还是又加了时再煮，直到水浓缩到一半，才感觉酸梅汤不那么淡而寡味，煮好后连渣晾凉也许是为了让渣的余味充分渗出，但也没感觉酸梅汤因此增色多少。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (961, '冰爽西瓜汁', '/api/img/2021080616282427962841958079.jpg', 16.00, '饮品', '原料：西瓜。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (962, '暴打柠檬冷泡茶', '/api/img/2021080616282366954321958079.jpg', 44.00, '饮品', '原料：香水柠檬、冷泡茶包、冰块、蜂蜜百香果冰、纯净水、蜂蜜、甘蔗糖浆。', '“据说这是最近很流行的网红茶哦。我用的无籽香水柠檬，味道肯定比普通柠檬好。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (963, '香醇原味豆浆', '/api/img/2021080616282137515861958079.jpg', 17.00, '饮品', '原料：黄豆、小米、糯米、纯净水。', '“收到美天兑换的九阳破壁机，迫不及待的想做豆浆喝，想到就动手，泡豆做起来。用破壁机做豆浆真的太方便了，九阳大品牌更是没得说静音不吵，豆浆，浓，醇，香，更营养健康！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (964, '青桔桃罐头冰水', '/api/img/2021080516281411362591958079.jpg', 25.00, '饮品', '原料：腌的青桔及汤汁、桃子罐头、矿泉水、蜂蜜。', '“腌的青桔可以食用了，放了点煮好的桃子罐头，做成水果冰水来喝。罐头和腌青桔是冰的，可以用常温的矿泉水，也可以用冰的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (965, '榨西瓜汁', '/api/img/2021080416280851262951958079.jpg', 56.00, '饮品', '原料：西瓜、白糖。', '“每次买来的西瓜是不是常有吃不完的感觉，这时就可以换个吃法——榨西瓜汁。好做、好喝，还省得浪费了西瓜。不用放水，这才是纯纯的鲜榨果汁。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (966, '酸梅汤', '/api/img/2021080416280797119671958079.jpg', 59.00, '饮品', '原料：乌梅、山楂、桑葚、玫瑰茄、陈皮、薄荷、冰糖、水、桂花。', '“妈妈还在的时候就特别喜欢喝这个，没想到自己去做，都是用酸梅膏兑水喝的。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (967, '玉米汁', '/api/img/2021080416280411491591958079.jpg', 16.00, '饮品', '原料：玉米、纯净水、白糖。', '“美味营养又健康的早餐，风味清香可口。甜玉米的水分比较多，口感很甜，做玉米汁特别合适。浓浓的玉米香气，百分百原汁原味，营养健康又美味。夏天冰着喝，口感也非常好。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (968, '百香果气泡水', '/api/img/2021080316279935759168197577.jpg', 20.00, '饮品', '原料：百香果、小青柑、雪碧、蜂蜜、柠檬。', '“炎热的天气来一杯清爽的百香果气泡水让人感觉清凉一夏 。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (969, '神仙茶', '/api/img/2021080316279547168638197577.jpg', 30.00, '饮品', '原料：山楂、桑叶、枸杞、菊花、普洱、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (970, '椰汁葡萄凉粉', '/api/img/2021080316279545927858197577.jpg', 48.00, '饮品', '原料：葡萄、白凉粉、清水、椰汁。', '“炎炎夏日，出趟门都像洗了个桑拿浴一样，这时候最幸福的事情就是窝在空调房里一边追剧一边享受下午茶了。今天用葡萄做一个适合夏天的小饮品，一起享受属于自己的惬意时光吧！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (971, '薏米玫瑰花茶', '/api/img/2021080216279130526701958079.jpg', 59.00, '饮品', '原料：玫瑰花、炒薏米、陈皮、纯净水、金花。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (972, '菊花黑枸杞绿茶饮', '/api/img/2021080216278947035108197577.jpg', 25.00, '饮品', '原料：菊花、黑枸杞、绿茶、清水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (973, '蜜糖腌青桔', '/api/img/2021080216278745752668197577.jpg', 56.00, '饮品', '原料：小青桔、香水柠檬、冰糖、蜂蜜。', '“买了一份小青桔，用蜂蜜和冰糖腌了，可以用来做各种水果茶。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (974, '冰镇牛奶鸡蛋西米露', '/api/img/2021080216278686373151958079.jpg', 49.00, '饮品', '原料：西米、鸡蛋、牛奶、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (975, '翻车的蜂蜜柠檬水', '/api/img/2021080116278061158801958079.jpg', 45.00, '饮品', '原料：干柠檬片、冰块、蜂蜜。', '“炎炎夏日，电费已经超出预算，胃口也没有，就来点酸甜的柠檬水叭，这里我用的是干柠檬片，做法真的非常简单。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (976, '自制好喝的青提果酒', '/api/img/2021080116278076183941958079.jpg', 59.00, '饮品', '原料：青提、黄糖、谷养康粮食酒。', '“哈喽大家好，今天我要跟大家分享一款女生喝的青提果酒，颜色也太适合夏天啦！酒香浓郁，醇香爽口，做法简单，喜欢的一定要试一试哦！️准备好食材️：青提300g，黄糖100g，谷养康粮食酒泡果酒专用酒2瓶。1、青提用盐水洗净，擦干水分，对半切开。2、准备一个干净无水无油的玻璃罐备用。3、罐子里放入一层青提，一层冰糖，再放一层青提，再放一层冰糖，最后倒入粮食酒，盖上盖子放阴凉干燥处，密封保存一个月即可！温馨提示：泡酒大家一定要用泡果酒专用粮食酒，泡出的酒才好喝，夏天喝的时候记得一点冰块更好喝哦！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (977, '如何制作猕猴桃果酒', '/api/img/2021080116278075536241958079.jpg', 28.00, '饮品', '原料：猕猴桃、冰糖、谷养康粮食果酒。', '“猕猴桃是一种营养特别丰富的水果，吃剩下的可以拿来泡果酒，不论是自己喝还是招待亲朋好友都特别赞，下面就跟我一起学如何泡制好喝的猕猴桃果酒吧。材料：猕猴桃：3-4个，冰糖110克，谷养康粮食果酒2瓶做法：1、密封罐用热水烫一下，自然晾干，必须保证无油无水。2、猕猴桃去皮，切片。3、罐子里放上一层猕猴桃，撒一层冰糖，再放上剩下的猕猴桃，撒冰糖。4、倒入谷养康粮食酒，盖好盖子，阴凉干燥处泡制一个月左右就可以饮用了。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (978, '蟠桃酸奶奶昔', '/api/img/2021080116277983017931958079.jpg', 50.00, '饮品', '原料：蟠桃、酸奶。', '“正是蟠桃大量上市的季节，蟠桃个又大又甜，配了酸奶打成奶昔，清凉又解暑。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (979, '黄皮爆柠', '/api/img/2021080116277906202761958079.jpg', 55.00, '饮品', '原料：黄皮、柠檬、茉莉绿茶茶包、糖浆、冰块。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (980, '黄桃气泡水', '/api/img/2021080116277890032591958079.jpg', 55.00, '饮品', '原料：黄蟠桃、蜂蜜火龙果冰、雪碧、香水柠檬。', '“天热，不想吃饭，别说做饭了。这种黄蟠桃直接吃就很甜，只是还想喝点冷饮，就把二者放一起了，喝了水再吃水果，就是我的一餐。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (981, '水果冰茶', '/api/img/2021080116277884557901958079.jpg', 57.00, '饮品', '原料：茶叶、青桔、香水柠檬、油蟠桃、西柚、芒果、无花果、纯净水、蜂蜜、冰糖。', '“买了一份小青桔，做了一份蜜糖腌青桔，还剩下几个。想想做杨枝甘露还剩下的西柚和芒果，干脆做个水果茶吧。我几乎不喝茶的，上午喝一杯，晚上也失眠。所以只放了小许茶叶，我也不知道是啥茶（有知道的望不吝赐教），老公喝的啥我就取了点啥。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (982, '清热解毒润肺的绿豆百合饮', '/api/img/2021073116277323128898197577.jpg', 30.00, '饮品', '原料：绿豆、干百合、大米、水、蜂蜜(选放)。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (983, '奶茶冻', '/api/img/2021072116268457127051958079.jpg', 43.00, '饮品', '原料：红茶包、牛奶、白凉粉、白糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (984, '西瓜冻撞奶', '/api/img/2021072016267680173768197577.jpg', 20.00, '饮品', '原料：西瓜、白凉粉、白砂糖、纯净水、冰牛奶。', '“夏天是西瓜大量上市的季节，今天用西瓜做一款好吃的甜品，简单又好吃，夏日解暑佳品。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (985, '樱桃果冻撞奶', '/api/img/2021072016267535303358197577.jpg', 22.00, '饮品', '原料：樱桃果酱、白凉粉、脱脂牛奶、纯净水、白糖。', '“冰箱塞满了，想做冷饮，没地方放啦，就把樱桃果酱拿出来，做成果冻撞奶吧。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (986, '酸梅汤', '/api/img/2021072016267484194458197577.jpg', 21.00, '饮品', '原料：乌梅、洛神花、甘草、山楂、陈皮、冰糖、水量。', '“用真材实料熬出来的酸梅汤，洛神花煮出来的天然的颜色，如红宝石般，颜色是红亮的。喝起来酸甜爽口，滋味浓郁，还带有乌梅微微的烟熏味。酸梅汤有生津止渴，开胃健脾的功效，总之比喝饮料好很多~” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (987, '花生扁豆豆浆', '/api/img/2021072016267454902731958079.jpg', 29.00, '饮品', '原料：花生、小扁豆、水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (988, '酸梅汤', '/api/img/2021072016267456392848197577.jpg', 18.00, '饮品', '原料：乌梅、乌枣、山楂、玫瑰茄、橘皮、甘草、桂花、冰糖、清水、冰块。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (989, '南瓜芒果牛奶糊', '/api/img/2021071916266639021371958079.jpg', 15.00, '饮品', '原料：南瓜、芒果肉、纯牛奶。', '“昨天做南瓜饼，蒸的南瓜多了，做芒果牛奶时顺便把南瓜也放进去打了，没放之前还是有点犹豫的，因为我家小孩对南瓜不感兴趣，放了怕她们不喝，没想到打出来后都喝不出来南瓜的味道，反而是更浓稠带点沙沙的感觉！成功骗到两个小家伙吃南瓜！因为家里种的是比较大的南瓜，昨天开一个，绝对用不完，就连削好皮的也没用完，今天早上就拿来继续打南瓜芒果牛奶糊了。晚上预约好的，锅里还放了两颗鸡蛋，早上起来，南瓜熟了，鸡蛋好了，直接切芒果打就可以了，一顿早餐，有牛奶鸡蛋，有水果有南瓜，也算比较均衡了，就是缺点肉。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (990, '西瓜酸奶奶昔', '/api/img/2021071816265986958258197577.jpg', 39.00, '饮品', '原料：西瓜、酸奶。', '““清凉消暑”酸酸甜甜，冰凉爽滑，清凉夏日饮品，粉粉的颜色使人陶醉！” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (991, '金砖奶茶', '/api/img/2021071816265810402818197577.jpg', 57.00, '饮品', '原料：白凉粉、红糖、牛奶、茶叶、糖。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (992, '清凉消暑：西米椰汁水果', '/api/img/2021071716265053473641958079.jpg', 50.00, '饮品', '原料：西米、芒果、红肉火龙果、炼奶、椰汁。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (993, '茉莉蜜茶', '/api/img/2021071716264928189111958079.jpg', 14.00, '饮品', '原料：茉莉花茶包、蜂蜜。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (994, '奶茶西米露', '/api/img/2021071716264921039401958079.jpg', 56.00, '饮品', '原料：西米、红茶、白糖、老红糖、牛奶、清水。', '“既有牛奶的香浓，又有红茶的甘醇，再加上西米的点缀，三者的结合，竟能让味道和口感如此惊艳。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (995, '芒果冰茶', '/api/img/2021071716264833489411958079.jpg', 27.00, '饮品', '原料：芒果、雪碧、黄瓜、养乐多、冰块。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (996, '火龙果蜂蜜冰茶', '/api/img/2021071616264244924221958079.jpg', 29.00, '饮品', '原料：火龙果、蜂蜜、薄荷叶、凉白开、冰块。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (997, '清凉消暑：姜枣茶', '/api/img/2021071616264018434261958079.jpg', 17.00, '饮品', '原料：生姜、红枣、饮用水、老红糖。', '“夏天最好的养生方法之一就是喝姜枣茶。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (998, '火龙果香蕉奶昔', '/api/img/2021071616264004882878197577.jpg', 48.00, '饮品', '原料：火龙果、牛奶、香蕉、无。', '“夏日凉清饮品～ 火龙果香蕉奶昔，粉粉嫩嫩的，香甜丝滑，给炎热的夏天带来一丝丝清凉～” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (999, '西瓜气泡水', '/api/img/2021071516263214479361958079.jpg', 32.00, '饮品', '原料：西瓜、气泡水或气泡弹、薄荷冰块、冻蜂蜜百香果、酸奶水果冰块。', '“空调西瓜，三伏天的绝美搭配哦。我体寒，不热狠了不去开空调，就来个西瓜气泡水吧。冰冰凉凉又酸酸甜甜的，好舒爽。” 					', NULL, NULL, 1);
INSERT INTO `foods` VALUES (1000, '烧仙草奶茶', '/api/img/2021071316261688274128197577.jpg', 37.00, '饮品', '原料：烧仙草、牛奶、红茶、葡萄干、桃肉、蜂蜜、纯净水。', '', NULL, NULL, 1);
INSERT INTO `foods` VALUES (1001, '姜枣茶', '/api/img/2021071316261569861088197577.jpg', 15.00, '饮品', '原料：红枣、姜丝、蜂蜜、纯净水。', '“常言道：“冬吃萝卜夏吃姜，身体越吃越健康”，夏天常饮姜枣茶，袪湿寒的功效，记得早上喝哟！” 					', NULL, NULL, 1);
